      /* smooth hash link scroll */
      $('.smooth-scroll').click(function() {
        if (location.pathname.replace(/^\//,'') === this.pathname.replace(/^\//,'') && location.hostname === this.hostname) {

          var target = $(this.hash);
          target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
          if (target.length) {
            $('html,body').animate({
              scrollTop: target.offset().top
            }, 1000);
            return false;
          }
        }
      });       

      /* data-href links */
      $('[data-href]').css('cursor', 'pointer');
      $('[data-href]').click(function(e) {
        if ($(this).attr('data-target') || e.shiftKey || e.ctrlKey || e.metaKey) {
          window.open($(this).attr('data-href'), $(this).attr('data-target'));
        } else {
          window.location.href = $(this).attr('data-href');
        }
      });
      $('[data-toggle="tooltip"]').tooltip();           

      $('[data-time-format]').each(function() {
          var el = $(this);
          switch(el.attr("data-time-format")) {
              case "time-ago":
              var timeValue = el.attr("data-time-value")
              var strTimeAgo = moment.unix(timeValue).fromNow();
              el.text(strTimeAgo);
              break;
          }
      });

      /* NAV BAR */
            $('#about-launcher').click(function() {showTab('about')});
            $('#board-launcher').click(function() {showTab('board')});
            $('#schools-launcher').click(function() {showTab('schools')});
            $('#parents-launcher').click(function() {showTab('parents')});
            $('#students-launcher').click(function() {showTab('students')});
            $('#staff-launcher').click(function() {showTab('staff')});

      function hideTab() {
        $('#page').removeClass('tab-open');
        $('.tweet-wrap').css('z-index','101');
        openNavTab = false;
                          $('#about-tab').css('display','none');
        $('#about-launcher').removeClass('active');
        $('#about-launcher').unbind('click');
        $('#about-launcher').click(function() {showTab('about')});   
                                                      $('#schools-tab').css('display','none');
        $('#schools-launcher').removeClass('active');
        $('#schools-launcher').unbind('click');
        $('#schools-launcher').click(function() {showTab('schools')});   
                                    $('#parents-tab').css('display','none');
        $('#parents-launcher').removeClass('active');
        $('#parents-launcher').unbind('click');
        $('#parents-launcher').click(function() {showTab('parents')});   
                                    $('#students-tab').css('display','none');
        $('#students-launcher').removeClass('active');
        $('#students-launcher').unbind('click');
        $('#students-launcher').click(function() {showTab('students')});   
                                    $('#staff-tab').css('display','none');
        $('#staff-launcher').removeClass('active');
        $('#staff-launcher').unbind('click');
        $('#staff-launcher').click(function() {showTab('staff')});   

      }
      function showTab(tabName) {
        if (!$('#'+tabName+'-launcher').hasClass('launch-external')) {
          hideTab();
          openNavTab = tabName;
          $('#'+tabName+'-tab').css('display','block');
          $('#page').addClass('tab-open');
          $('#'+tabName+'-launcher').addClass('active');
          $('#'+tabName+'-launcher').unbind('click');
          $('#'+tabName+'-launcher').click(hideTab);
          $('.tweet-wrap').css('z-index','0');
          if (tabName == 'schools') {
            if ($('#schools-tab iframe').attr('src') == '') {
              $('#schools-tab iframe').attr('src','https://www.google.com/maps/d/u/2/embed?mid=19qjTOTlWvrWgns6PzzDGGe5M-4k');              
            }
          }
        }
      }       
      $('.nav a').on('click', function(){
        if($('.navbar-toggle').css('display') !=='none'){
          $(".navbar-toggle").click();
        }
      });
      $('.tab-content .icon-close').each(function() { 
        $(this).click(function() {hideTab()});
      });    

      function loadNavMenu() {
        $('#nav-menu #menu-list').html('');
        $('#topbar .topnav > div').each(function() {
          if ($(this).attr('id')) {
            if ($(this).attr('id').indexOf('launcher') != -1 && $(this).text().trim() != 'Back to Kaneland') {
              if ($(this).hasClass('launch-external')) {
                if ($(this).hasClass('launch-internal')) {
                  if ($(this).attr('data-toggle') == 'modal') {
                    $('#nav-menu #menu-list').append(''+$(this).text()+'');                    
                  } else if ($(this).attr('onclick')) {
                    $('#nav-menu #menu-list').append(''+$(this).text()+'');                    
                  } else {
                    $('#nav-menu #menu-list').append(''+$(this).text()+'');                    
                  }
                } else {
                  $('#nav-menu #menu-list').append(''+$(this).text()+'');
                }
              } else {
                $('#nav-menu #menu-list').append(''+$(this).text()+'');
                $('#mobile-launcher-'+$(this).attr('id').split('-')[0]).unbind('click');
                $('#mobile-launcher-'+$(this).attr('id').split('-')[0]).click(function() {
                  setupViewForMobileNavTab($(this).attr('id').split('-')[2]);
                });                
              }
            }            
          }
        });
        if ($('body').hasClass('school-site')) {
          $('#nav-menu #menu-list').append('District Home');          
        }
                $('#nav-menu #menu-list > a').each(function() {
          $(this).css('opacity','0');
        });
        $('#nav-menu').css('display','block');
        $('body').css('height', $(window).height() + 'px');
        $('body').css('overflow', 'hidden');
        $('#nav-menu #menu-list > a').each(function() {
          $(this).css({ opacity: 1, transition: 'opacity 2s' });  
        });        
      }

      function setupViewForMobileNavTab(currentTab) {
          showTab(currentTab);
          $('#'+currentTab+'-tab').css('overflow','auto');
          if ($('body').hasClass('calendar') || $('body').hasClass('calendar-mobile') || $('body').hasClass('staff-directory')) {
            $('#'+currentTab+'-tab').css('height',($(window).height() - 50) + 'px');
          } else {
            $('#'+currentTab+'-tab').css('height',($(window).height() - 80) + 'px');
          }
          $('#mobile-menu').unbind('click');
          $('#mobile-menu').click(function() {
            openMobileMenu();
            hideTab(currentTab);
          });
          $('#nav-menu').css('display','none');
          $('#mobile-menu .fa').removeClass('fa-bars');
          $('#mobile-menu .fa').addClass('fa-chevron-left');        
      }

      function destroyViewForMobileNavTab(currentTab) {
          $('#'+currentTab+'-tab').css('overflow','hidden');
          $('#'+currentTab+'-tab').css('height', '450px');     
      }      

      function hideNavMenu() {
        $('#nav-menu').css('display','none');
        $('body').css('height', 'auto');
        $('body').css('overflow', 'auto');        
      }  

      $('.search-wrap .fa-search').click(loadMobileSearch);   

      if ($('#hubnut-wrap').length > 0) {
        if (!isFlashEnabled()) {
          $('#hubnut-wrap iframe').css('display','none');
          $('#hubnut-wrap > div').css('margin-top','70px');
          $('#hubnut-wrap button').html('Watch on ');
        }
      }



      function isFlashEnabled() {
          var hasFlash = false;
          try {
            var fo = new ActiveXObject('ShockwaveFlash.ShockwaveFlash');
            if(fo) hasFlash = true;
          }
          catch(e){
            if(navigator.mimeTypes ["application/x-shockwave-flash"] != undefined) hasFlash = true;
          }
          return hasFlash;
      }

      // http://stackoverflow.com/questions/46155/validate-email-address-in-javascript
      function validateEmail(email) {
          var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
          return re.test(email);
      } 

      function hideBlocks(blocks, offset) {
        console.log(blocks,offset);
        blocks.each(function(){
          ( $(this).offset().top > $(window).scrollTop()+$(window).height()*offset ) && $(this).find('.cd-timeline-img, .cd-timeline-content').addClass('is-hidden');
        });
      }

      function showBlocks(blocks, offset) {
        blocks.each(function(){
          ( $(this).offset().top <= $(window).scrollTop()+$(window).height()*offset && $(this).find('.cd-timeline-img').hasClass('is-hidden') ) && $(this).find('.cd-timeline-img, .cd-timeline-content').removeClass('is-hidden').addClass('bounce-in');
        });
      }   

      function loadGrantAppModal() {
        $('body').css('overflow','hidden');
        $('#grant-modal').modal('show');
        $('#grant-modal').on('hidden.bs.modal', function (e) {
          $('body').css('overflow','auto');
        });    
      }  

      $('#event-modal').on('shown.bs.modal', function () {
        setTimeout(function() {
            $('#event-modal .modal-dialog').removeClass('redraw');
            $('#event-modal .modal-dialog').addClass('redraw');
            $('.modal-body, .modal-header, .modal-footer').append('
');
        }, 400);        
      });

      $('#event-modal').on('hidden.bs.modal', function () {
        $('.modal-body, .modal-header, .modal-footer').find('.change').remove();
      });

      $('#foia-modal').on('shown.bs.modal', function () {
        $('#foia-modal .modal-dialog').removeClass('redraw');
        $('#foia-modal .modal-dialog').addClass('redraw');
      });   

