		                "imageNum960"           : 5, //NUMBER OF IMAGES TO DISPLAY AT 960
		                "imageNum768"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 768
		                "imageNum640"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 640
		                "imageNum480"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 480
		                "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
		                "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		                "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		                "buttonColor"           : "#7C878E", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
		                "extraBackIconClass"    : "sw-icon",
		                "extraNextIconClass"    : "sw-icon",
		                "controlGutter"         : "20" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
		        });

                $(".hp .column.full .ui-widget.app.headlines").each(function() {
                	var thisApp = this;
                    $(thisApp).find(".ui-widget-footer").appendTo(thisApp);
                });
		}

		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "right-to-left", // OPTIONS - left-to-right, right-to-left
			"primaryColor"		: "#171C8F", // DEFAULT - #2C363E
		        "secondaryColor"	: "#1221DD", // DEFAULT - #222A30
		        "accentColor"		: "#FBE122", // DEFAULT - #B5BBBF
			"menuButtonParent"	: "#gb-channel-list",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: "yes",
			"showTranslate"		: "no",
			"translateId"		: "none",
			"showAccount"		: "no",
			"extraMenuOptions"	: "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
			"breakPoint"		: 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"primaryColor"		: "#171C8F", // DEFAULT - #2C363E
		        "secondaryColor"	: "#1221DD", // DEFAULT - #222A30
		        "accentColor"		: "#FBE122", // DEFAULT - #B5BBBF
			"menuButtonParent"	: "#sp-content",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"		: "4"
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();

        csGlobalJs.OpenInNewWindowWarning();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			mediaRotator();
			//DOUBLE ROW CHANNEL BAR
			secondRowChannels();
			if(!$(".hp .column.full .ui-widget.app .ui-article").size() > 5) {
				$(".hp .column.full .headlines-scroll-ctrl.back").hide();
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			mediaRotator();
			appView();
			if(!$(".hp .column.full .ui-widget.app .ui-article").size() > 3) {
				$(".hp .column.full .headlines-scroll-ctrl.back").hide();
			}
			if($("#gb-header-outer").hasClass("fixed")) {
				$("#gb-header-outer").removeClass("fixed");
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			mediaRotator();
			appView();
			if(!$(".hp .column.full .ui-widget.app .ui-article").size() > 3) {
				$(".hp .column.full .headlines-scroll-ctrl.back").hide();
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			mediaRotator();
			appView();
			if(!$("#rs-pagelist-menu-btn").hasClass("ui-clear")) {
				$("#rs-pagelist-menu-btn").addClass("ui-clear");
			}
			if(!$(".hp .column.full .ui-widget.app .ui-article").size() > 2) {
				$(".hp .column.full .headlines-scroll-ctrl.back").hide();
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			mediaRotator();
			appView();
			if(!$("#rs-pagelist-menu-btn").hasClass("ui-clear")) {
				$("#rs-pagelist-menu-btn").addClass("ui-clear");
			}
			if(!$(".hp .column.full .ui-widget.app .ui-article").size() > 1) {
				$(".hp .column.full .headlines-scroll-ctrl.back").hide();
			}
		}
	}

	function secondRowChannels() {
		var contWidth = $("#gb-channel-list").width();
		var totalWidth = 0;
		$("#gb-channel-list li.sw-channel-item").each(function() {
			totalWidth += $(this).outerWidth();
		});
		if(totalWidth > contWidth) {
			$("#gb-channel-list").addClass("wrap");
		}
	}

	function mediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth" 			: 1500,
		        	"imageHeight" 			: 915,
		        	"appHeader" 			: "no",
		        	"titles" 			: "yes",
		        	"captions" 			: "yes",
		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "no",
		        	"mainControls" 			: "yes",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "Read More",
		        	"descMoreLink" 			: "yes",
		        	"descMoreLinkText" 		: "5",
		        	"bullets" 			: "no",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "yes", // image dimensions work well at 1500x915
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "fade", // fade or slide
		        	"transitionSpeed" 		: 3,
		        	"transitionDelay" 		: 8,
				"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() {},
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() { 
		        		//MOVE READ MORE LINK
		        		if($(".rs-gallery-desc-more-link").length) {
						$(".rs-gallery-desc-more-link").addClass("icon-font").prependTo("#hp-slideshow .rs-title h1");
					}
		        	},
		        	"onTransitionEnd" 		: function() {
		        		// SET PREV AND NEXT CONTROL IMAGES
		        		var prevImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.prev img").attr("src");
		        		var prevImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.prev img").attr("title");
		      			var nextImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.next img").attr("src");
		      			var nextImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.next img").attr("title");
		        		$("#hp-slideshow .rs-gallery-control.back .rs-gallery-control-img img").attr("src", prevImgSrc).attr("title", prevImgTitle).attr("alt", prevImgTitle);
		        		$("#hp-slideshow .rs-gallery-control.next .rs-gallery-control-img img").attr("src", nextImgSrc).attr("title", nextImgTitle).attr("alt", nextImgTitle);
		        	 },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() {
					// MOVE SLIDESHOW CONTROLS
				        $("#hp-slideshow .rs-gallery-images").prepend($("#hp-slideshow .rs-gallery-control.next")).prepend($("#hp-slideshow .rs-gallery-control.back"));

				        //MOVE READ MORE LINK
		        		if($(".rs-gallery-desc-more-link").length) {
						$(".rs-gallery-desc-more-link").addClass("icon-font").prependTo("#hp-slideshow .rs-title h1");
					}

					// SET PREV AND NEXT CONTROL IMAGES
		        		var prevImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.prev .media-src").text();
		        		var prevImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.prev .media-title").text();
		      			var nextImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.next .media-src").text();
		      			var nextImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.next .media-title").text();
		        		$("#hp-slideshow .rs-gallery-control.back").append('
');
		        		$("#hp-slideshow .rs-gallery-control.next").append('
');

		        	 	// SET CONTROL HOVER
		        		$(".rs-gallery-control").hover(function() {
		        			$(".rs-gallery-control-img", this).fadeIn("fast");
		        		}, function () {
		        			$(".rs-gallery-control-img", this).stop(true, true).fadeOut("fast");
		        		});
		        	 }
			});
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content-bottom div.ui-widget.app.active").length){
			$("#hp-content-bottom div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content-bottom div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC move mystart buttons

			if($(".sw-mystart-button.register").length) {
				$(".sw-mystart-button.register a").wrap("
").parent().prependTo("#cs-user-account ul");
			}
			$(".sw-mystart-button.signin a").wrap("
").parent().prependTo("#cs-user-account ul");


		//DOC user account toggle
		$(".gb-icon.account").click(function(e) {
			e.preventDefault();
			$("#cs-schools, #cs-translate").slideUp();
			if($(".gb-icon.account").hasClass("open")) {
                $(".gb-icon.account").removeClass("open").attr({
                        "aria-expanded": "false",
                });
                $("#cs-user-account").removeClass("open").attr({
                        "aria-hidden": "true"
                }).slideUp();
                $("#cs-user-account li a").attr("tabindex","-1");
            } else {
                $(".gb-icon.account").addClass("open").attr({
                    "aria-expanded": "true",
                });
                $("#cs-user-account").addClass("open").attr({
                        "aria-hidden": "false"
                }).slideDown();
                $("#cs-user-account li a").attr("tabindex","0");
            }
		});

        //ACCOUNT A11Y
        $(".gb-icon.account").keydown(function(e) {
            switch(e.keyCode) {
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-user-account ul li:first-child > a").focus();
                break;

                case csKeyCodes.up:
                    e.preventDefault();
                    $("#cs-user-account ul li:last-child > a").focus();
                break;

                case csKeyCodes.space:
                    e.preventDefault();
                    if($(".gb-icon.account").hasClass("open")) {
                    	$(".gb-icon.account").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-user-account").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-user-account li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.account").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-user-account").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-user-account li a").attr("tabindex","0");
                    }
                break;

                case csKeyCodes.enter:
                    e.preventDefault();
                    if($(".gb-icon.account").hasClass("open")) {
                    	$(".gb-icon.account").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-user-account").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-user-account li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.account").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-user-account").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-user-account li a").attr("tabindex","0");
                    }
                break;
            }
        });

        $("#cs-user-account a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            console.log("keydown");
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".gb-icon.account").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-user-account ul li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#cs-user-account ul li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#cs-user-account ul li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#cs-user-account ul > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.esc:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.account").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-user-account").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-user-account li a").attr("tabindex","-1");
                    $(".gb-icon.account").focus();
                break;

                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.account").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-user-account").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-user-account li a").attr("tabindex","-1");
                    $(".gb-icon.home").focus();
                break;

            }
        });

		//TOGGLE SEARCH
		$(".gb-icon.search").click(function(e) {
        	e.preventDefault();
            if($(this).attr("aria-expanded") == "false") {
                $(this).attr("aria-expanded", "true").addClass("open");
                $("#gb-search-outer").attr("aria-hidden", "false").slideDown(function() {
                    $("#sw-search-input").focus();
                });
            } else {
                $(this).attr("aria-expanded", "false").removeClass("open");
                $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
            }
        });

        $(".gb-icon.search").keydown(function(e) {
            switch(e.keyCode) {
            	case csKeyCodes.space:
                    e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true").addClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "false").slideDown(function() {
                            $("#sw-search-input").focus();
                        });
                    } else {
                        $(this).attr("aria-expanded", "false").removeClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
                    }
                break;

                case csKeyCodes.enter:
                    e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true").addClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "false").slideDown(function() {
                            $("#sw-search-input").focus();
                        });
                    } else {
                        $(this).attr("aria-expanded", "false").removeClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
                    }
                break;
            }
        });

        $("#sw-search-button").keydown(function(e) {
        		switch(e.keyCode) {
                	case csKeyCodes.tab:
                    	e.preventDefault();
                        if(e.keyCode = csKeyCodes.shift) {
                        	$("#sw-search-input").focus();
                        } else {
                        	$(".gb-icon.search").focus();
                        }
                    break;
                }
        });

        $(".gb-icon.search").focusout(function() {
        	setTimeout(function() {
                    if(!$("#gb-search-outer").find(":focus").length) {
                        $(".gb-icon.search").attr("aria-expanded", "false").removeClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
                    }
                }, 500);
        });

        $("#gb-search-outer").focusout(function() {
            	setTimeout(function() {
                    if(!$("#gb-search-outer").find(":focus").length && !$(".gb-icon.search").is(":focus")) {
                        $(".gb-icon.search").attr("aria-expanded", "false").removeClass("open");
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
                    }
                }, 500);
            }).keydown(function(e) {
            	if(e.keyCode == csKeyCodes.esc) {
                	e.preventDefault();

                    $(".gb-icon.search").attr("aria-expanded", "false").focus().removeClass("open");
                    $("#gb-search-outer").attr("aria-hidden", "true").slideUp();
                }
            });

		//TOGGLE SCHOOL LIST DROPDOWN
		$(".gb-icon.schools").click(function(e) {
			e.preventDefault();
			$("#cs-user-account, #cs-translate").slideUp();
			if($(".gb-icon.schools").hasClass("open")) {
                $(".gb-icon.schools").removeClass("open").attr({
                        "aria-expanded": "false",
                });
                $("#cs-schoollist").removeClass("open").attr({
                        "aria-hidden": "true"
                }).slideUp();
                $("#cs-schoollist li a").attr("tabindex","-1");
            } else {
                $(".gb-icon.schools").addClass("open").attr({
                    "aria-expanded": "true",
                });
                $("#cs-schoollist").addClass("open").attr({
                        "aria-hidden": "false"
                }).slideDown();
                $("#cs-schoollist li a").attr("tabindex","0");
            }
		});

        //SCHOOLS A11Y
        $(".gb-icon.schools").keydown(function(e) {
            switch(e.keyCode) {
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-schoollist ul li:first-child > a").focus();
                break;

                case csKeyCodes.up:
                    e.preventDefault();
                    $("#cs-schoollist ul li:last-child > a").focus();
                break;

                case csKeyCodes.space:
                    e.preventDefault();
                    if($(".gb-icon.schools").hasClass("open")) {
                    	$(".gb-icon.schools").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-schoollist").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-schoollist li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.schools").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-schoollist").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-schoollist li a").attr("tabindex","0");
                    }
                break;

                case csKeyCodes.enter:
                    e.preventDefault();
                    if($(".gb-icon.schools").hasClass("open")) {
                    	$(".gb-icon.schools").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-schoollist").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-schoollist li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.schools").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-schoollist").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-schoollist li a").attr("tabindex","0");
                    }
                break;
            }
        });

        $("#cs-schoollist ul li > a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".gb-icon.schools").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-schoollist ul li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#cs-schoollist ul li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#cs-schoollist ul li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#cs-schoollist ul > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.esc:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.schools").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-schoollist").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-schoollist li a").attr("tabindex","-1");
                    $(".gb-icon.schools").focus();
                break;

                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.schools").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-schoollist").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-schoollist li a").attr("tabindex","-1");
                    $(".gb-icon.search").focus();
                break;

            }
        });

		//HIDE CURRENT SCHOOL IN SCHOOLLIST DROPDOWN
		var siteAlias = "leyden";
		switch(siteAlias) {
			case "leyden":
				$("#cs-schoollist li.district-school").remove();
			break;
			case "East":
				$("#cs-schoollist li.east-school").remove();
			break;
			case "West":
				$("#cs-schoollist li.west-school").remove();
			break;
		}

		//TRANSLATE DROPDOWN
		var pageURL = window.location.href;
		var translateDropdown = '' +
						'
