        $(".gb-icon.translate").click(function(e) {
			e.preventDefault();
			$("#cs-user-account, #cs-schools").slideUp();
			if($(".gb-icon.translate").hasClass("open")) {
                $(".gb-icon.translate").removeClass("open").attr({
                        "aria-expanded": "false",
                });
                $("#cs-translate").removeClass("open").attr({
                        "aria-hidden": "true"
                }).slideUp();
                $("#cs-translate li a").attr("tabindex","-1");
            } else {
                $(".gb-icon.translate").addClass("open").attr({
                    "aria-expanded": "true",
                });
                $("#cs-translate").addClass("open").attr({
                        "aria-hidden": "false"
                }).slideDown();
                $("#cs-translate li a").attr("tabindex","0");
            }
		});

        //SCHOOLS A11Y
        $(".gb-icon.translate").keydown(function(e) {
            switch(e.keyCode) {
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-translate ul li:first-child > a").focus();
                break;

                case csKeyCodes.up:
                    e.preventDefault();
                    $("#cs-translate ul li:last-child > a").focus();
                break;

                case csKeyCodes.space:
                    e.preventDefault();
                    if($(".gb-icon.translate").hasClass("open")) {
                    	$(".gb-icon.translate").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-translate").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-translate li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.translate").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-translate").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-translate li a").attr("tabindex","0");
                    }
                break;

                case csKeyCodes.enter:
                    e.preventDefault();
                    if($(".gb-icon.translate").hasClass("open")) {
                    	$(".gb-icon.translate").removeClass("open").attr({
                                "aria-expanded": "false",
                        });
                        $("#cs-translate").removeClass("open").attr({
                                "aria-hidden": "true"
                        }).slideUp();
                        $("#cs-translate li a").attr("tabindex","-1");
                    } else {
                    	$(".gb-icon.translate").addClass("open").attr({
                            "aria-expanded": "true",
                        });
                        $("#cs-translate").addClass("open").attr({
                                "aria-hidden": "false"
                        }).slideDown();
                        $("#cs-translate li a").attr("tabindex","0");
                    }
                break;
            }
        });

        $("#cs-translate ul li a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".gb-icon.translate").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#cs-translate ul li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#cs-translate ul li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#cs-translate ul li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#cs-translate ul > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.esc:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.translate").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-translate").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-translate li a").attr("tabindex","-1");
                    $(".gb-icon.translate").focus();
                break;

                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-icon.translate").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#cs-translate").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#cs-translate li a").attr("tabindex","-1");
                    $(".gb-icon.account").focus();
                break;

            }
        });

