function toggle_resources(detailid)
{
    if(document.getElementById("res_" + detailid).style.display != "block")
    {
        document.getElementById("res_" + detailid).style.display="block";
    }
    else
    {
        document.getElementById("res_" + detailid).style.display="none";
    }
}


	
	function event_view_CMS120138(eventid) {
		windowprops = "top=0,left=0,width=600,height=500,resizable=yes,scrollbars=yes,menubar=yes";
		url = "http://www.baugo.org/pro/event_view.cfm?memberid=1066&eventid=" + eventid;
		window.open(url, "event"+eventid, windowprops);
	}
