jQuery(window).on('load', function () {

	jQuery('#menu-main-menu > li').each(function( index ) {
		var $this         = jQuery(this),
				scrollTop     = jQuery(window).scrollTop(),
				elementOffset = $this.offset().top,
				distance      = (elementOffset - scrollTop),
				wpadminbar    = jQuery('#wpadminbar');

		$this.on('hover', function () {
			jQuery('#menu-main-menu > li > ul.sub-menu').css('top', -Math.abs(distance));
		});

		// Account for admin bar height if it exists
		if (wpadminbar.length === 1){
			distance = distance - wpadminbar.height();
		}

		// Set top margin for each sub menu
		$this.find('ul.sub-menu > li:first-child').css('margin-top', distance);

		// console.log(distance);
	});

	jQuery(window).on('scroll', function () {
		jQuery('#slidingbar-area').removeClass('open');
	});

});

div.script:
var btn_left  = jQuery(".xeno--snav-left");
var btn_right = jQuery(".xeno--snav-right");
var counter1   = 1;

var btn_left2  = jQuery(".xeno--snav-left-2");
var btn_right2 = jQuery(".xeno--snav-right-2");
var counter2   = 1;


// Events
btn_left.on("click", function(e){
	iterate("left", e, counter1);
});
btn_right.on("click", function(e){
	iterate("right", e, counter1);
});

btn_left2.on("click", function(e){
	iterate("left", e, counter2);
});
btn_right2.on("click", function(e){
	iterate("right", e, counter2);
});

// Functions
function iterate(direction, e, counter) {
	var parent = '#' + jQuery('.' + e.target.className).closest(".xeno--graph").attr('id');
	var num    = jQuery(parent + ' .xeno--desc').length;
	console.log(parent);

	// Iterate counter
	if (direction == "left") {
		counter -= 1;
	} else if (direction == "right") {
		counter += 1;
	}

	 // Reset counter
	if (counter > num) {
		counter = 1;
	} else if (counter <= 0) {
		counter = num;
	}

	// Update global counter
	if (parent == "#slider1") {
		counter1 = counter;
	} else if (parent == "#slider2") {
		counter2 = counter;
	}

	// Do stuff with counter
	var target_img  = parent + ' .xeno--i-' + counter;
	var target_desc = parent + ' .xeno--d-' + counter;

	// Remove 'active' from all current images and descriptions
	jQuery(parent + ' .xeno--img').removeClass('active');
	jQuery(parent + ' .xeno--desc').removeClass('active');

	// Add 'active' to correct image and description
	jQuery(target_img).addClass('active');
	jQuery(target_desc).addClass('active');

	console.log(counter);
}

