        $(document).ready(function () {
            SetCookie('SWScreenWidth', screen.width);
            SetCookie('SWClientWidth', document.body.clientWidth);
            
            $("div.ui-article:last").addClass("last-article");
            $("div.region .app:last").addClass("last-app");

            // get on screen alerts
            var isAnyActiveOSA = 'True';
            var onscreenAlertCookie = GetCookie('Alerts');

            if (onscreenAlertCookie == '' || onscreenAlertCookie == undefined) {
                onscreenAlertCookie = "";
            }
            if (isAnyActiveOSA == 'True') {
                GetContent(homeURL + "/cms/Tools/OnScreenAlerts/UserControls/OnScreenAlertDialogListWrapper.aspx?OnScreenAlertCookie=" + onscreenAlertCookie + "&SiteID=4", "onscreenalert-holder", 2, "OnScreenAlertCheckListItem();");
            }

            

        });

    // ADA SKIP NAV
    $(document).ready(function () {
        $(document).on('focus', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "0px"
            }, 500);
        });

        $(document).on('blur', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "-30px"
            }, 500);
        });
    });

    // ADA MYSTART
    $(document).ready(function () {
        var top_level_nav = $('.sw-mystart-nav');

        // Set tabIndex to -1 so that top_level_links can't receive focus until menu is open
        // school dropdown
        $(top_level_nav).find('ul').find('a').attr('tabIndex', -1);

        // my account dropdown
        $(top_level_nav).next('ul').find('a').attr('tabIndex', -1);

        var openNavCallback = function(e, element) {
             // hide open menus
            hideMyStartBarMenu();

            // show school dropdown
            if ($(element).find('ul').length > 0) {
                $(element).find('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).find('.sw-dropdown').find('li:first-child a').focus()
            }

            // show my account dropdown
            if ($(element).next('ul').length > 0) {
                $(element).next('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).next('.sw-dropdown').find('li:first-child a').focus();
                $('#sw-mystart-account').addClass("clicked-state");
            }
        }

        $(top_level_nav).click(function (e) {
            openNavCallback(e, this);
        });

        $('.sw-dropdown-list li').click(function(e) {
            e.stopImmediatePropagation();
            $(this).focus();
        });
        
        // Bind arrow keys for navigation
        $(top_level_nav).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).prev('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').last().focus();
                } else {
                    $(this).prev('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }

                // show my account dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).next('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').first().focus();
                } else {
                    $(this).next('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }

                // show my account dropdown
                if ($(this).next('ul').length > 0) {
                    $(this).next('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                
                openNavCallback(e, this);
                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideMyStartBarMenu();
            } else {
                $(this).parent('.sw-mystart-nav').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        // school dropdown
        var startbarlinks = $(top_level_nav).find('ul').find('a');
        bindMyStartBarLinks(startbarlinks);

        // my account dropdown
        var myaccountlinks = $(top_level_nav).next('ul').find('a');
        bindMyStartBarLinks(myaccountlinks);

        function bindMyStartBarLinks(links) {
            $(links).keydown(function (e) {
                e.stopPropagation();

                if (e.keyCode == 38) { //key up
                    e.preventDefault();

                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                    e.preventDefault();
                    hideMyStartBarMenu();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;

                    $(this).parent('div').nextAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            found = true;
                            return false;
                        }
                    });

                    if (!found) {
                        $(this).parent('div').prevAll('li').find('a').each(function () {
                            if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                return false;
                            }
                        });
                    }
                }
            });
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('#sw-mystart-inner').find('.sw-mystart-nav').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() { 
            hideMyStartBarMenu();
        });*/

        // try to capture as many custom MyStart bars as possible
        $('.sw-mystart-button').find('a').focus(function () {
            hideMyStartBarMenu();
        });

        $('#sw-mystart-inner').click(function (e) {
            e.stopPropagation();
        });

        $('ul.sw-dropdown-list').blur(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-mypasskey').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-sitemanager').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-myview').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-signin').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-register').focus(function () {
            hideMyStartBarMenu();
        });

        // button click events
        $('div.sw-mystart-button.home a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.pw a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.manage a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#sw-mystart-account').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).addClass('clicked-state');
                $('#sw-myaccount-list').show();
            }
        });

        $('#sw-mystart-mypasskey a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.signin a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.register a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });
    });

    function hideMyStartBarMenu() {
        $('.sw-dropdown').attr('aria-hidden', 'true').css('display', 'none');
        $('#sw-mystart-account').removeClass("clicked-state");
    }

    // ADA CHANNEL NAV
    $(document).ready(function() {
        var channelCount;
        var channelIndex = 1;
        var settings = {
            menuHoverClass: 'hover'
        };

        // Add ARIA roles to menubar and menu items
        $('[id="channel-navigation"]').attr('role', 'menubar').find('li a').attr('role', 'menuitem').attr('tabindex', '0');

        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        channelCount = $(top_level_links).length;


        $(top_level_links).each(function() {
            $(this).attr('aria-posinset', channelIndex).attr('aria-setsize', channelCount);
            $(this).next('ul').attr({ 'aria-hidden': 'true', 'role': 'menu' });

            if ($(this).parent('li.sw-channel-item').children('ul').length > 0) {
                $(this).attr('aria-haspopup', 'true');
            }

            var sectionCount = $(this).next('ul').find('a').length;
            var sectionIndex = 1;
            $(this).next('ul').find('a').each(function() {
                $(this).attr('tabIndex', -1).attr('aria-posinset', sectionIndex).attr('aria-setsize', sectionCount);
                sectionIndex++;
            });
            channelIndex++;

        });

        $(top_level_links).focus(function () {
            //hide open menus
            hideChannelMenu();

            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'false').css('display', 'block');
            }
        });

        // Bind arrow keys for navigation
        $(top_level_links).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').find('> li').last().find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').find('> li').first().find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li')
                         .addClass(settings.menuHoverClass)
                         .find('ul.sw-channel-dropdown').css('display', 'block')
                         .attr('aria-hidden', 'false')
                         .find('a').attr('tabIndex', 0)
                         .first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').addClass(settings.menuHoverClass).find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideChannelMenu();
            } else {
                $(this).parent('li').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        var links = $(top_level_links).parent('li').find('ul').find('a');

        $(links).keydown(function (e) {
            if (e.keyCode == 38) {
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) {
                e.preventDefault();

                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 27 || e.keyCode == 37) {
                e.preventDefault();
                $(this).parents('ul').first().prev('a').focus().parents('ul').first().find('.' + settings.menuHoverClass).removeClass(settings.menuHoverClass);
            } else if (e.keyCode == 32) {
                e.preventDefault();
                window.location = $(this).attr('href');
            } else {
                var found = false;

                $(this).parent('li').nextAll('li').find('a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        found = true;
                        return false;
                    }
                });

                if (!found) {
                    $(this).parent('li').prevAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            return false;
                        }
                    });
                }
            }
        });

        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).removeClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'true').css('display', 'none').find('a').attr('tabIndex', -1);
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('[id="channel-navigation"]').find('a').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideChannelMenu();
            }
        });

        $('[id="channel-navigation"]').find('a').first().keydown(function (e) {
            if (e.keyCode == 9) {
                // hide open MyStart Bar menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() {
            hideChannelMenu();
        });*/

        $('[id="channel-navigation"]').click(function (e) {
            e.stopPropagation();
        });
    });

    $(document).ready(function() {
        $('input.required').each(function() {
            if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                    $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                }
            }
        });

        $(document).ajaxComplete(function() {
            $('input.required').each(function() {
                if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                    if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                        $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                    }
                }
            });
        });
    });

    $(function() {
    	CreativeTemplate.Init();


    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Slm();
            this.CoolTools();
            this.GlobalIcons();
            this.Header();
            this.ChannelBar();
            this.ArticleSlider();
            //this.AppScroller();
            this.Body();
            this.Footer();
            this.Slideshow();
            this.AppAccordion();
            this.Search();
            this.modEvents();

            this.JsMediaQueries();
            this.CsCustomADA();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	// HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

        	var _this = this;
            this.CalendarListView();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            //DOC unbind system hover and add custom hover event
			$("ul.sw-channel-list li.sw-channel-item").unbind("hover").unbind("on").unbind("click");

            //DOC unbind system search function and add custom search function
            $("#sw-search-button").unbind().removeAttr("onclick").attr("href", "#");
            $("#sw-mystart-search").on("click keydown", "#sw-search-button", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();

                    if($.trim($("#sw-search-input").val()) != "Search..." && $.trim($("#sw-search-input").val()) != "") {
                        SWGoToSearchResultsPageswsearchinput();
                    }
                }


            });

            //UNBIND SYSTEM SEARCH AND PREVENT USERS FROM SUBMITTING AN EMPTY FORM
            $("input#sw-search-input").unbind();
            $("input#sw-search-input").on("keydown", function(e) {
                if(e.keyCode === 13) {
                    e.preventDefault();

                    if($.trim($("#sw-search-input").val()) != "Search..." && $.trim($("#sw-search-input").val()) != "") {
                        SWGoToSearchResultsPageswsearchinput();
                    }
                }


            });

            //KEEP TAB INSIDE FLYOUT MENU
            $('#sw-search-button').on('keydown', function(e) {
            	if(e.keyCode === 9) {
                	$('.gb-flyout-menu-tab:eq(1)').focus();
                }
            })


        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.AppArrowPlacement();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					desktopCalendarLink();
                    if (!$("#gb-global-icons .customize-button").length) {
                    	$("CUSTOMIZE SITE").appendTo($(".hp #gb-global-icons"));
                	}
                break;
                case "768":
					MoveCalendarLink();
                    if (!$("#gb-global-icons .customize-button").length) {
                    	$("CUSTOMIZE SITE").appendTo($(".hp #gb-global-icons"));
                	}
                break;
                case "640":
                	if ($("#gb-global-icons .customize-button").length) {
                    	$(this).remove();
                    }
                break;
                case "480":
					if ($("#gb-global-icons .customize-button").length) {
                    	$(this).remove();
                    }
                break;
                case "320":
					if ($("#gb-global-icons .customize-button").length) {
                    	$(this).remove();
                    }
                break;
            }

            function MoveCalendarLink() {
                //MOVE UPCOMING EVENTS VIEW CALENDAR LINK BESIDE APP HEADER 
                $('.ui-widget.app.upcomingevents').each(function() {
                    var viewCalendarLink = $(this).find($(".view-calendar-link"));
                    var header = $(this).find($(".ui-widget-header"));
                    (viewCalendarLink).appendTo(header);
                });
            }

            function desktopCalendarLink() {
                $('.ui-widget.app.upcomingevents').each(function() {
                    if ($(".ui-widget-header .view-calendar-link", this).length) {
                        $(".view-calendar-link", this).appendTo($(".ui-widget-detail", this));
                    }
                });
            }
        },

		"Slm": function() {
            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 1312,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.gb-flyout-schools-sub-section ul').html(schools);
                }
            });
        },

        "MyStart": function() {
        	// ADD TRANSLATE
        	$("#gb-mystart .translate-container").creativeTranslate({
                "type": 1,
                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "Translate",
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true, 
                        "translateItemsList": true,
                        "customElementMarkup": '
' +
                                                  '
translate' +
                                                  '
' +
                                                      '' +
                                                      	'[$CreativeTranslateListItems$]' +
                                                      '' +
                                                  '' +
                                              ''
                    }
               }
            });


            $("#cs-user-options-list").append('
' + $(".sw-mystart-button.signin").html() + '');

            if($(".sw-mystart-button.register").length) {
            	$("#cs-user-options-list").append('
' + $(".sw-mystart-button.register").html() + '');
            }


            this.MyStartDropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "MyStartDropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            /* ASSUMES THE FOLLOWING STRUCTURE



User Options




Link



            */

        	var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(document).on("click", dropdownSelector, function(e) {
                e.preventDefault();
                var thisLabel = $(this).attr("aria-label");

                if($(this).parent().hasClass("open")) {
                	thisLabel = thisLabel.replace("Collapse", "Expand");
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	thisLabel = thisLabel.replace("Expand", "Collapse");
                    $(this).attr({"aria-expanded": "true", "aria-label": thisLabel}).parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing", function(){
                        $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                    });
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        var thisLabel = $(this).attr("aria-label");

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	thisLabel = thisLabel.replace("Collapse", "Expand");
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	thisLabel = thisLabel.replace("Expand", "Collapse");
                            $(this).attr({"aria-expanded": "true", "aria-label": thisLabel}).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }

                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();

                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(document).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	e.preventDefault();

                    	if(e.shiftKey) {
                        	// IS FIRST ITEM
                            if($(this).parent().is(":first-child")) {
                                // FOCUS DROPDOWN BUTTON
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).closest(dropdownParent).find(dropdownSelector).focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                            }
                        } else {
                        	// IS LAST ITEM
                            if($(this).parent().is(":last-child")) {
                                // FOCUS FIRST ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                                $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                            }
                        }
                    break;


                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");

                    break;


                }

                $(dropdownParent).mouseleave(function(e) {

					$(dropdownList + " a", this).attr("tabindex", "-1");
					$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");

            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
            });


        },

        "Header": function() {

            function csGetBreakPoint() {
                return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
            }

            $(".gb-flyout-menu-close-button").attr('tabindex', '-1');

            //DOC open flyout menu on enter
            $(".gb-flyout-menu-tab").on('keyup', function(e) { // Trigger the click event from the keyboard
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    $(this).click();
                }
            });

            //DOC open flyout menu
            $(".gb-flyout-menu-tab").on("click", function() {
            	if ($("#gb-page").hasClass("open-menu") && !$(this).hasClass("gb-flyout-menu-selected")) { 
                    $(".gb-flyout-menu-tab").removeClass("gb-flyout-menu-selected");
                    $(this).addClass("gb-flyout-menu-selected");
                    $(".gb-flyout-menu-section").addClass("hidden");
                    $(".gb-flyout-menu-section").eq($(this).index()).removeClass("hidden");
                    $(".gb-flyout-menu-section").eq($(this).index()).attr("aria-hidden", "false");
                    $(".gb-flyout-menu-close-button").attr('tabindex', '0');
                    $(".gb-flyout-menu-section").eq($(this).index()).focus($("a"));

                    return false;
              	}
              	if (!$("#gb-page").hasClass("open-menu")) {
                    $("#gb-page").addClass("open-menu");
                    $(".gb-flyout-menu-tab").removeClass("gb-flyout-menu-selected");
                    $(this).addClass("gb-flyout-menu-selected");
                    $(".gb-flyout-menu-tab").attr("aria-expanded", "false");
                    $(".gb-flyout-menu-tab").attr("aria-expanded", "true");
                    $(".gb-flyout-menu-section").addClass("hidden");
                    $(".gb-flyout-menu-section").eq($(this).index()).removeClass("hidden");
                    $(".gb-flyout-menu-section").eq($(this).index()).attr("aria-hidden", "false");
                    $(".gb-flyout-menu-close-button").attr('tabindex', '0');
                    $(".gb-flyout-menu-section").eq($(this).index()).focus($("a"));

                        //IF TAB IS SELECTED, GIVE ALL CORRESPONDING SECTION LIST ITEMS A TAB INDEX OF ZERO
                        if ($(this).hasClass("gb-flyout-menu-selected")) {
                            if ($(this).hasClass("gb-schools-tab")) {
                                $(".gb-flyout-schools-sub-section a").attr("tabindex", 0)
                            } else {
                                $(".gb-flyout-schools-sub-section a").attr("tabindex", -1)
                            }
                        }
                        if(csGetBreakPoint() == "desktop") {
                          if($("#gb-flyout-menu").hasClass("open")) {
                              //FOCUS FIRST ITEM IN FLYOUT SECTION
                              if ($(".gb-flyout-menu-tab").hasClass("gb-flyout-menu-selected")) {
                                  if ($(".gb-flyout-menu-tab").hasClass("gb-explore-tab")) {
                                      $("ul.sw-channel-list li.sw-channel-item:first-of-type a").focus();
                                  }
                                  if ($(".gb-flyout-menu-tab").hasClass("gb-schools-tab")) {
                                      $(".gb-flyout-schools-sub-section li:first-of-type a").focus();
                                  }
                                  if ($(".gb-flyout-menu-tab").hasClass("gb-explore-tab")) {
                                      $("#sw-search-input").focus();
                                  }
                                  if ($(".gb-flyout-menu-tab").hasClass("gb-popular-tab")) {
                                      $("#gb-cool-tools .global-icon:first-of-type").focus();
                                  }
                              }
                      	} else {
                            $("#gb-flyout-menu").addClass("open");
                            $("#gb-flyout-menu").animate({
                                right: 0
                            }, 1000, function() {

                            });
                      		$("#gb-flyout-menu-tabs").animate({
                          		right: 403
                      		}, 1000, function() {
                                //FOCUS FIRST ITEM IN FLYOUT SECTION
                                if ($(".gb-flyout-menu-tab").hasClass("gb-flyout-menu-selected")) {
                                    if ($(".gb-flyout-menu-tab").hasClass("gb-explore-tab")) {
                                        $("ul.sw-channel-list li.sw-channel-item:first-of-type a").focus();
                                    }
                                    if ($(".gb-flyout-menu-tab").hasClass("gb-schools-tab")) {
                                        $(".gb-flyout-schools-sub-section li:first-of-type a").focus();
                                    }
                                    if ($(".gb-flyout-menu-tab").hasClass("gb-explore-tab")) {
                                        $("#sw-search-input").focus();
                                    }
                                    if ($(".gb-flyout-menu-tab").hasClass("gb-popular-tab")) {
                                        $("#gb-cool-tools .global-icon:first-of-type").focus();
                                    }
                                }
                      		});
                      		$("#gb-flyout-menu-tabs").addClass("open");
                   		}
               		} else {
                   		var menuPos = $("#gb-mystart-outer").height()+ $("#gb-header-outer").height()+$("#gb-flyout-menu-tabs").height();
                   		$("#gb-flyout-menu").addClass("open");
                   			$("#gb-flyout-menu").animate({
                       			top: menuPos
                   			}, 1000, function() {

                   		});
              		}
          		} else {
              		$("#gb-page").removeClass("open-menu");
              		$(".gb-flyout-menu-tab").removeClass("gb-flyout-menu-selected");
              		$(".gb-flyout-menu-tab").attr("aria-expanded", "false");
              		$("#gb-flyout-menu-tabs").removeClass("open"); 
              		if(csGetBreakPoint() == "desktop") {
                        $("#gb-flyout-menu").animate({
                            right: -403
                        }, 1000, function() {
                        	$("#gb-flyout-menu").removeClass("open");
                    	});
                    	$("#gb-flyout-menu-tabs").animate({
                        	right: 0
                    	}, 1000, function() {
                        	$(".gb-flyout-menu-section").attr("aria-hidden", "true");
                        	$("#gb-flyout-menu-tabs").removeClass("open");
                    	});
                    } else {
                        $(".gb-flyout-menu-section").addClass("hidden").attr('tabindex', -1);
                        $("#gb-flyout-menu a").attr('tabindex', -1);  
                        $("#gb-flyout-menu").animate({
                           top: -840
                        }, 1000, function() {

                        });
                    }
          		}
      		});

            //DOC close flyout menu
            $(".gb-flyout-menu-close-button, #gb-page-overlay").on("click", function() {
                $("#gb-page").removeClass("open-menu");
                $(".gb-flyout-menu-tab").removeClass("gb-flyout-menu-selected");
                $("#gb-flyout-menu-tabs").removeClass("open"); 
                $(".gb-flyout-menu-tab").attr("aria-expanded", "false");
                $(".gb-flyout-menu-close-button").attr('tabindex', '-1');
                if(csGetBreakPoint() == "desktop") {
                      $("#gb-flyout-menu").animate({
                          right: -403
                      }, 1000, function() {
                          $("#gb-flyout-menu").removeClass("open");
                          $("#gb-flyout-menu").attr("aria-hidden", "true");

                      });
                      $("#gb-flyout-menu-tabs").animate({
                          right: 0
                      }, 1000, function() {
                          $("#gb-flyout-menu-tabs").removeClass("open");
                          setTimeout(($(".gb-explore-tab").focus()), 5000);
                      });
                } else {
                    $(".gb-flyout-menu-section").addClass("hidden").attr('tabindex', -1);
                    $("#gb-flyout-menu a").attr('tabindex', -1);  
                    $("#gb-flyout-menu").animate({
                       top: -840
                    }, 1000, function() {

                    });
                }
            });

            //WHEN PRESSING TAB ON ACTIVE TAB, FOCUS FIRST LIST ITEM
            $(".gb-flyout-menu-tab").on('keydown', function(e) {
                if ((e.keyCode == 9) && ($(this).is(":focus"))) {
                    if ($(".gb-flyout-menu-tab").hasClass("gb-flyout-menu-selected")) {
                        e.preventDefault();
                        $(".gb-flyout-menu-section").eq($(this).index()).find(':focusable').eq(0).focus();

                        //WHEN PRESSING SHIFT + TAB ON ACTIVE TAB, FOCUS LAST LIST ITEM
                        if(e.shiftKey) {
                            e.preventDefault();
                           $(".gb-flyout-menu-section").eq($(this).index()).find(':focusable').last().focus();
                        }
                    }
                }
                if (e.keyCode == 38) { //UP ARROW
                    e.preventDefault();
                    $(this).prev(".gb-flyout-menu-tab").focus();
                }
                if (e.keyCode == 40) { //DOWN ARROWS
                    e.preventDefault();
                    $(this).next(".gb-flyout-menu-tab").focus();
                }
            });

            //DO NOT ALLOW SHIFT + TAB TO WORK ON THE FLYOUT SECTION CLOSE BUTTON
            $(".gb-flyout-menu-close-button").on('keydown', function(e) {
                var code = e.which;
                if (e.keyCode == 9) { // TAB

                    if(e.shiftKey) {
                        e.preventDefault();
                       $(".gb-flyout-menu-close-button").focus();
                    }
                }
            });

            //CALL CLICK EVENT FOR CLOSE BUTTON ON KEYPRESS
            $(".gb-flyout-menu-close-button").on('keydown', function(e) {
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 13) || (code === 32)) {
                    e.preventDefault();
                    $(this).click();
                }
            });



            //WHEN PRESSING TAB ON SEARCH BUTTON, FOCUS THE ACTIVE TAB
            $("#sw-search-button").on('keydown', function(e) {
                var code = e.which;
                if (e.keyCode === 9) { // TAB
                    e.preventDefault();
                    $(".gb-search-tab").focus();
                    if(e.shiftKey) {
                        e.preventDefault();
                       $("#sw-search-input").focus();
                    }
                }
                if ((e.keyCode === 13) || (e.keyCode === 32)) {
                    e.preventDefault();
                    $(this).click();
                }
            });

            //WHEN PRESSING TAB + SHIFT ON FIRST GLOBAL ICON, GO BACK TO ROTATOR READ MORE
            $(".hp #gb-global-icons .global-icon:first-of-type").on('keydown', function(e) {
                var code = e.which;
                if (e.keyCode == 9 && (e.shiftKey)) { // TAB
                    e.preventDefault();
                    $("#hp-slideshow .rs-gallery-desc-more-link a").focus();
                }
            });

            //WHEN PRESSING TAB ON LAST CHANNEL LIST ITEM, FOCUS THE ACTIVE TAB
            $(".sw-channel-item:last-of-type a").on('keydown', function(e) {
                var code = e.which;
                if (e.keyCode == 9 && (!e.shiftKey)) {
                    e.preventDefault();
                    $(".gb-explore-tab").focus();
                }
            });

            //KEYDOWN FOR SCHOOLS FLYOUT MENU
            $("#sw-channel-list-container li a").on('keydown', function(e) {
                var code = e.which;
                if($(this).parent().is(":last-child")) {
                    if (e.keyCode == 9 && (!e.shiftKey)) {
                        e.preventDefault();
                        $(".gb-explore-tab").focus();
                    }
                }
                if($(this).parent().hasClass("has-dropdown")) {
                    var parentMenuItem = $(this).parent();
                    parentMenuItem.find('.sw-channel-dropdown li:first-of-type a').focus();

                }
                if (e.keyCode == 38) { //key up
                    e.preventDefault();
                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 37) { // key left
                    e.preventDefault();
                } else if (e.keyCode == 39) { // key right
                    e.preventDefault();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;
                }
            });

            //KEYDOWN FOR SCHOOLS FLYOUT MENU
            $(".gb-flyout-schools-sub-section li a").on('keydown', function(e) {
                var code = e.which;
                if($(this).parent().is(":last-child")) {
                    if (e.keyCode == 9 && (!e.shiftKey)) {
                        e.preventDefault();
                        $(".gb-schools-tab").focus();
                    }
                }
                if (e.keyCode == 38 || e.keyCode == 37) { //key up or key left
                    e.preventDefault();
                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        $(this).parents('ul').find('li:last-of-type a').focus();
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40 || e.keyCode == 39) { //key down or key right
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                            $(this).parents('ul').find('li:first-of-type a').focus();
                    } else {
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;
                }
            });

            //WHEN PRESSING TAB ON LAST COOL TOOLS ICON, FOCUS THE ACTIVE TAB
            $("#gb-cool-tools .global-icon").on('keydown', function(e) {
                var code = e.which;
                if($(this).is(":last-child")) {
                    if (e.keyCode == 9 && (!e.shiftKey)) { // TAB
                        e.preventDefault();
                        $(".gb-popular-tab").focus();
                    }
                }
                if (e.keyCode == 38 || e.keyCode == 37) { //key up or key left
                    e.preventDefault();
                    // This is the first item
                    if ($(this).prev('a').length == 0) {
                        $(this).parents('#gb-cool-tools').find('a:last-of-type').focus();
                    } else {
                        $(this).prev('a').focus();
                    }
                } else if (e.keyCode == 40 || e.keyCode == 39) { //key down or key right
                    e.preventDefault();

                    if ($(this).next('a').length == 0) {
                            $(this).parents('#gb-cool-tools').find('a:first-of-type').focus();
                    } else {
                        $(this).next('a').focus();
                    }
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;
                }
            });
        },

