        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //DOC add signed out breadcrumb
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            // ADJUST SIGN IN PAGE
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

            // ADJUST REGISTER PAGE
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            // ADJUST SEARCH RESULTS PAGE
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span").text("view all");

            //MOVE HEADLINES VIEW ALL LINK BESIDE APP HEADER 
            $('.ui-widget.app.headlines').each(function() {
             	var moreLink = $(this).find($(".more-link"));
                var header = $(this).find($(".ui-widget-header"));
                (moreLink).appendTo(header);
            });

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //KEYBOARD NAVIGATION ON SUBPAGE
            //TAB ON USER OPTIONS GOES TO FIRST GLOBAL ICON
            $(".sp .user-options .cs-selector").on('keydown', function(e) {
            	if (e.keyCode == 9 && (!e.shiftKey)) {
                	e.preventDefault();
                    $("#gb-global-icons .global-icon:first-of-type").focus();
                }
            });

            //TAB + SHIFT ON FIRST GLOBAL ICON GOES TO USER OPTIONS
            $(".sp #gb-global-icons .global-icon:first-of-type").on('keydown', function(e) {
            	if (e.keyCode == 9 && e.shiftKey) {
      				e.preventDefault();
                    $(".sp .user-options .cs-selector").focus();
                }
            });

            //TAB ON LAST GLOBAL ICON GOES TO MENU TAB
            $(".sp #gb-global-icons .global-icon:last-of-type").on('keydown', function(e) {
            	if (e.keyCode == 9 && (!e.shiftKey)) { // TAB
                		e.preventDefault();
                        $(".gb-explore-tab").focus();
                }
            });

            //TAB + SHIFT ON MENU TAB GOES TO LAST GLOBAL ICON
            $(".sp .gb-explore-tab").on('keydown', function(e) {
            	if (e.keyCode == 9 && (e.shiftKey)) {
                    e.preventDefault();
                    $(".sp #gb-global-icons .global-icon:last-of-type").focus();
                }
            });

            //TAB FROM COOL TOOLS TAB GOES TO FIRST FOCUSABLE ITEM IN SUBPAGE CONTENT
            $(".sp .gb-popular-tab").on('keydown', function(e) {
            	if (e.keyCode == 9 && (!e.shiftKey)) { // TAB
      				e.preventDefault();
                    $("main").find(':focusable').eq(0).focus();
                }
            });

            $('.app.flexpage h2:empty').remove();
        },

        "Footer": function() {
            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());

            //REMOVE F: AND DIVIDER BULLET IF FAX IS EMPTY
            if ( $('.fax-number').text().length == 0 ) {
                $(".fax").hide();
            }

            //BACK TO TOP BUTTON FUNCTIONALITY   
            $(document).on("click keydown", "#back-to-top-btn", function(e) {
                if(e.type == "click" || e.type == "keydown" && e.keyCode == this.KeyCodes.space || e.type == "keydown" && e.keyCode == this.KeyCodes.enter) {
                    e.preventDefault();

                    $("html, body").animate({
                        scrollTop: $("body").offset().top
                    }, "slow", function() {
                        $(".sw-mystart-button.home a").focus();
                    });
                }
            });

            //ONLY LET MAP SCROLL AFTER ITS BEEN CLICKED
            $("#unclicked").on('click', function() {
            		$(this).hide();
                    $(".gb-footer-top.one").css("visibility", "none");
            });

            //ADD ARIA LABEL TO SITE MAP LINK
            $("#gb-footer-bottom .gb.footer.two a").attr("aria-label", "Site Map");
        },

