        "GalleryDesc": function(phase){
        	//we switch on the phase of the gallery
        	switch(phase){
            	case "start":
                	//when the gallery starts it's animation we hide the description so we can process it's information.
                	$('#hp-slideshow .rs-gallery-desc').fadeOut(300);
                    break;

                case "end":
                	//this timeout allows the gallery animations to finish, since classing of elements doesn't happen until that ends.
                	setTimeout(function(e){
                    	if( !$('#hp-slideshow .rs-title.active').hasClass('hidden') ){ //if the user hasn't chosen to hide the title...
                        	$('#hp-slideshow .custom-title')
                            	.removeClass('hidden') // remove our hidden class incase it was there.
                                .html($('#hp-slideshow .rs-title.active h1').html()); //put the active titles HTML into our custom element.

                        }else{
                        	$('#hp-slideshow .custom-title').addClass('hidden'); //if they did choose to hide the title then we will hide ours as well for ADA purposes.
                        }

                        //this is exactly the same as the title to the T.
                        if( !$('#hp-slideshow .rs-caption.active').hasClass('hidden') ){
                        	$('#hp-slideshow .custom-caption')
                            	.removeClass('hidden')
                                .html($('#hp-slideshow .rs-caption.active p').html());

                        }else{
                        	$('#hp-slideshow .custom-caption').addClass('hidden');
                        }

                        //once we've finished processing we fade the description back in.
                        $('#hp-slideshow .rs-gallery-desc').fadeIn(300);
                    }, 300);
                	break;

                default:
                	break;
            }
        },

