                    break;
                    case "feb":
                      ariaMonth = "February";
                    break;
                    case "mar":
                      ariaMonth = "March";
                    break;
                    case "apr":
                      ariaMonth = "April";
                    break;
                    case "may":
                      ariaMonth = "May";
                    break;
                    case "jun":
                      ariaMonth = "June";
                    break;
                    case "jul":
                      ariaMonth = "July";
                    break;
                    case "aug":
                      ariaMonth = "August";
                    break;
                    case "sep":
                      ariaMonth = "September";
                    break;
                    case "oct":
                      ariaMonth = "October";
                    break;
                    case "nov":
                      ariaMonth = "November";
                    break;
                    case "dec":
                      ariaMonth = "December";
                    break;
                    default: ariaMonth = eventMonth;
                  }
                  switch(eventNumberDay){
                    case "1":
                      ariaDate = "First";
                    break;
                    case "2":
                      ariaDate = "Second";
                    break;
                    case "3":
                      ariaDate = "Third";
                    break;
                    case "4":
                      ariaDate = "Fourth";
                    break;
                    case "5":
