        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "CsCustomADA": function() {
            //OPEN CUSTOMIZATION OPTIONS
            $(".gb-link.customize").click(function(e) {
                e.preventDefault();
                if ($(".gb-link.customize").hasClass('open')) {
                	$('.customize-button').attr('aria-expanded', 'false');
                } else {
                	$('.customize-button').attr('aria-expanded', 'true');
                }
                $(this).toggleClass("open");
                $("#gb-customize-outer").slideToggle();
            });

            //CONTRAST OPTIONS
            $(".contrast.black-yellow").click(function(e) {
                e.preventDefault();
                eraseCookie("whiteBlack");
                eraseCookie("blackWhite");
                if($("body").hasClass("contrast-blackYellow")) {
                    $("body").removeClass("contrast-blackYellow");
                    eraseCookie("blackYellow");
                } else {
                    className = "contrast-blackYellow";
                    $("body").removeClass("contrast-whiteBlack");
                    $("body").removeClass("contrast-blackWhite");
                    $("body").toggleClass(className);
                    createCookie("blackYellow",className);
                        return false;
                }
            });
            $(".contrast.white-black").click(function(e) {
                e.preventDefault();
                eraseCookie("blackYellow");
                eraseCookie("blackWhite");
                if($("body").hasClass("contrast-whiteBlack")) {
                    $("body").removeClass("contrast-whiteBlack");
                    eraseCookie("whiteBlack");
                } else {
                    className = "contrast-whiteBlack";
                    $("body").removeClass("contrast-blackYellow");
                    $("body").removeClass("contrast-blackWhite");
                    $("body").toggleClass(className);
                    createCookie("whiteBlack",className);
                        return false;
                    }
            });
            $(".contrast.black-white").click(function(e) {
                e.preventDefault();
                eraseCookie("blackYellow");
                eraseCookie("whiteBlack");
                if($("body").hasClass("contrast-blackWhite")) {
                    $("body").removeClass("contrast-blackWhite");
                    eraseCookie("blackWhite");
                } else {
                    className = "contrast-blackWhite";
                    $("body").removeClass("contrast-blackYellow");
                    $("body").removeClass("contrast-whiteBlack");
                    $("body").toggleClass(className);				
                    createCookie("blackWhite",className);
                        return false;
                    }
            });

            if (readCookie("blackYellow") != null) {
                $("body").removeClass("contrast-whiteBlack");
                $("body").removeClass("contrast-blackWhite");
                    $("body").addClass(readCookie("blackYellow"));

                } else if (readCookie("whiteBlack") != null) {
                $("body").removeClass("contrast-blackYellow");
                $("body").removeClass("contrast-blackWhite");
                    $("body").addClass(readCookie("whiteBlack"));

                } else if (readCookie("blackWhite") != null) {
                $("body").removeClass("contrast-blackYellow");
                $("body").removeClass("contrast-whiteBlack");
                    $("body").addClass(readCookie("blackWhite"));

                } else {
                    $("body").removeClass("contrast-blackYellow");
                $("body").removeClass("contrast-whiteBlack");
                $("body").removeClass("contrast-blackWhite");
                }

            //TEXT SIZE OPTIONS
            $("#custom-text-size .text.default").click(function(e) {
                e.preventDefault();
                $("body").removeClass("text-medium");
                $("body").removeClass("text-large");
                eraseCookie("textMedium");
                eraseCookie("textLarge");
            });
            $("#custom-text-size .text.medium").click(function(e) {
                e.preventDefault();
                var textSize = "text-medium";
                $("body").removeClass("text-large");
                $("body").toggleClass(textSize);
                createCookie("textMedium",textSize);
                    return false;

            });
            $("#custom-text-size .text.large").click(function(e) {
                e.preventDefault();
                var textSize = "text-large";
                $("body").removeClass("text-medium");
                $("body").toggleClass(textSize);
                createCookie("textLarge",textSize);
                    return false;
            });

                if (readCookie("textMedium") != null) {
                $("body").removeClass("text-large");
                    $("body").addClass(readCookie("textMedium"));

                } else if(readCookie("textLarge") != null) {
                $("body").removeClass("text-medium");
                    $("body").addClass(readCookie("textLarge"));

                } else {
                    $("body").removeClass("text-medium");
                $("body").removeClass("text-large");
                }

        	function createCookie(name, value, days) {
                var expires;

                if (days) {
                    var date = new Date();
                    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                    expires = "; expires=" + date.toGMTString();
                } else {
                    expires = "";
                }
                document.cookie = encodeURIComponent(name) + "=" + encodeURIComponent(value) + expires + "; path=/";
            }

            function readCookie(name) {
                var nameEQ = encodeURIComponent(name) + "=";
                var ca = document.cookie.split(';');
                for (var i = 0; i < ca.length; i++) {
                    var c = ca[i];
                    while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                    if (c.indexOf(nameEQ) === 0) return decodeURIComponent(c.substring(nameEQ.length, c.length));
                }
                return null;
            }

            function eraseCookie(name) {
                createCookie(name, "", -1);
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },
    };

