(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.8";
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

div.script:
jQuery(document).ready(function(){

	jQuery('#check li').keyup(function(e) {
		var n= jQuery(this).attr('class');
		jQuery('.uk-parent').removeClass("uk-open");
		
		jQuery('.tm-dropdown-overlay').removeClass("lock");
		jQuery(".uk-dropdown").css({"left": "0px"});
		jQuery(".tm-dropdown-overlay").css({"display": "block", "height": "0px"});
		
		if(n == 'uk-parent'){ 
			jQuery(this).addClass("uk-open");
			jQuery(".uk-parent a i").empty();
			
			var focused=document.activeElement.text;
			if(!focused){
			jQuery(".uk-dropdown").css({"left": "-149px"});
			jQuery(".tm-dropdown-overlay").addClass("lock");
			}
			jQuery(".tm-dropdown-overlay").css({"display": "block", "height": "299px"});
			jQuery(".lock").css({"display": "block", "height": "328px"});
		}
		
		var code = e.keyCode || e.which;
		
		if (code === 9) {  
			e.preventDefault();
		}
		
	});
});

body.script:
$.extend({
getUrlVars: function(){
var vars = [], hash;
var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
for(var i = 0; i < hashes.length; i++)
{
	hash = hashes[i].split('=');
	vars.push(hash[0]);
	vars[hash[0]] = hash[1];
}
return vars;
},
getUrlVar: function(name){
	return $.getUrlVars()[name];
}

});
function TranslateTo(language)
{
	var currentURL = window.location.href;

	if (language.match('original')) {
		currentURL = $.getUrlVar('u');
		parent.location = unescape(currentURL);
	}
	else {
		if (currentURL.indexOf('translate.google.com') > 0)
			currentURL = unescape($.getUrlVar('u'));

		parent.location='http://translate.google.com/translate?hl=' + language + '&sl=auto&tl=' + language + '&u=' + encodeURIComponent(currentURL);
	}
}
$(document).ready(function(){

		if ($(".languageDropdown").length > 0) {
			var currentURL = window.location.href;
			if (currentURL.indexOf('translate.google.com') > 0)
				document.getElementById('gtl_original').style.display = 'inline-block';
			else
				document.getElementById('gtl_original').style.display = 'none';
		}
});
