header.script:
jQuery(document).ready(function($){
	var dropdownOffset = function(){
		$('.has-children .dropdown').each(function(){
			var offset = $(this).parent().offset().left;
			var runover = $(window).width() - (offset + $(this).width() + parseInt($(this).css('padding-left')) + parseInt($(this).css('padding-right')));
			if(runover < 0){
				$(this).css('left',runover);
			}
			else if(parseInt($(this).css('left')) != 0){
				$(this).css('left',0);
			}
		});
	}
	$(window).load(function(){
		dropdownOffset();
	});
	$(window).resize(function(){
		dropdownOffset();
	});

	$('.navicon').click(function(){
		$('.bottom nav').addClass('mobile-show');
	});
	$('.navicon-close').click(function(){
		$('.bottom nav').removeClass('mobile-show');
	});

	$('nav > ul > .has-children:not(.quick-links) > a span').click(function(e){
		e.preventDefault();
		var parent = $(this).parent().parent();
		if($(window).width() < 890){
			if(parent.hasClass('open')){
				parent.removeClass('open');
			}else{
				$('.has-children.open').removeClass('open');
				parent.addClass('open');
			}
		}
	});

	$('nav > ul > .quick-links').click(function(){
		if($(this).hasClass('open')){
			$(this).removeClass('open');
		}else{
			$('.has-children.open').removeClass('open');
			$(this).addClass('open');
		}
	});

	$('#search-box').on('focus',
		function(){
			$('.search').addClass('open');
		}).on('blur', function(){
			setTimeout(function() {
				if($('.search').hasClass('open')){
					$('.search').removeClass('open');
				}
			}, 300);
	});

	$('.search-icon').click(function(){
		if($('.search').hasClass('open')){
			$('.search').removeClass('open');
		}else{
			$('.search').addClass('open');
			$('#search-box').focus();
		}
	});

	$(document).keyup(function(e){
		if(e.keyCode === 27 && $('.search').hasClass('open')){
			$('.search').removeClass('open');
		}
	})

	// Ensure that dropdown menus are accessible
    var menuTimeout;
    jQuery('.bottom nav ul a').on('blur', function() {
        menuTimeout = setTimeout(removeHoverState, 100);
    }).on('focus', function() {
        clearTimeout(menuTimeout);
        $navItem = jQuery(this).closest('.has-children');
        $navItem.addClass('hover');
        jQuery('.has-children').not($navItem).removeClass('hover');
    });
});

function removeHoverState() {
    var $nav = jQuery('.bottom nav'),
        $navItems = jQuery('.has-children', $nav),
        $dds = jQuery('.dropdown', $nav);
    $navItems.each(function() {
        jQuery(this).removeClass('hover');
    });
    $dds.each(function() {
        jQuery(this).attr('aria-hidden', true);
    });
}

div.script:
jQuery(document).ready(function($){
	var newsPosition = 1;
	$('.news-control.right').click(function(){
		if(newsPosition != 3){
			if($(window).width() > 1220){
				$('.news-rotator a:first-child').animate({marginLeft:'-=981px'},500);
			}else{
				$('.news-rotator a:first-child').animate({marginLeft:'-=102%'},500);
			}
			newsPosition++;
		}else if(newsPosition == 3){
			window.location = $(this).data('href');
		}
	});
	$('.news-control.left').click(function(){
		if(newsPosition != 1){
			if($(window).width() > 1220){
				$('.news-rotator a:first-child').animate({marginLeft:'+=981px'},500);
			}else{
				$('.news-rotator a:first-child').animate({marginLeft:'+=102%'},500);
			}
			newsPosition--;
		}
	});
	$(document).resize(function(){
		$('.news-rotator a:first-child').css('margin-left','0');
	});
});

