div.script:
jQuery(document).ready(function($){
	$('.quick-links h4').click(function(){
		if($(window).width() < 420){
			if($(this).hasClass('open')){
				$(this).removeClass('open');
				$('.quick-links a').css('display','none');
			}else{
				$(this).addClass('open');
				$('.quick-links a').css('display','block');
			}
		}
	});
	eventFilters.view = 'day';
	eventFilters.limit = 8;
	$(document).jbEvents({
		filters: eventFilters,
		map: eventTaxMap,
		tagTemplateId: 'evt-tag-template',
		debug: false
	});

	$('.home-hero__slideshow').slick({
		dots: false,
		speed: 1000,
		autoplay: true,
		autoplaySpeed: 10000
	});
});
