td.script:
//
  var GOVDSNIPPET = function() {
    var form  = document.getElementById('GD-snippet-form');
    var typeSelect = form.getElementsByTagName('select')[0];
    var getStyleType = function(type) {
      return typeSelect.value === type ? 'block' : 'none';
    };
    var toggleType = function() {
      form.getElementsByTagName('li')[2].style.display = getStyleType('email');
      form.getElementsByTagName('li')[1].style.display = getStyleType('phone');
    };
    if (typeSelect.addEventListener) {
      typeSelect.addEventListener('change', toggleType);
    } else if (typeSelect.attachEvent)  {
      typeSelect.attachEvent('onchange', toggleType);
    }
  }();
//

div.script:
;(function($) {
  'use strict';
  $(function() {
    // Wait until all of the images in the features are loaded
    $('.socs-feature-img').imagesLoaded(function() {
      $('.socs-feature.two').each(function() {
        var $this = $(this);

        // Add a border on the side of whichever box is the tallest - .two or .three
        if ($this.height() >= $this.next().height()) {
          $this.addClass('bordered');
        } else {
          $this.next().addClass('bordered');
        }
      });

      // If the last feature is the left-side of the "split" - undo the split
      var $lastFeature = $('.socs-feature:last');
      if ($lastFeature.hasClass('two')) {
        $lastFeature.css({
          'float': 'none',
          'padding-right': '0',
          'width': 'auto'
        }).removeClass('bordered');
      }
    });

  });
})(jQuery);

