    // https://stackoverflow.com/a/51789311/5629100
    var YoutubeVideos = (function () {
        var playersYoutube = [],	// players array (to control players individually)
            queueYoutube = [];		// videos queue (once api is ready, transform this into YT player)

        // Constructor
        function YoutubeVideos() { }

        // Add elements to queue
        YoutubeVideos.prototype.add = function ($video) {
            queueYoutube.push($video);
        };

        // Finds player based on the id of the iframe element
        YoutubeVideos.prototype.findPlayer = function (id) {
            var playerObj = null;

            playersYoutube.forEach(function (item) {
                if (item.playerId == id) {
                    playerObj = item.player;
                }
            });

            return playerObj;
        };

        // Load Youtube API
        YoutubeVideos.prototype.loadApi = function () {
            $.getScript("https://www.youtube.com/iframe_api", function () {
                window.onYouTubeIframeAPIReady = function () {
                    queueYoutube.forEach(function ($video) {
                        var loopBit = function () {
                            if ($video.data('loop-video') == true) {
                                return 1;
                            }
                            return 0;
                        };

                        var player = new YT.Player($video.get(0), {
                            width: $video.data('frame-width'),
                            height: $video.data('frame-height'),
                            videoId: $video.data('video-id'),
                            events: {
                                'onStateChange': onPlayerStateChange,
                                'onReady': onPlayerReady
                            },
                            playerVars: {
                                autoplay: 0,
                                rel: 0,
                                enablejsapi: 1,
                                loop: loopBit
                            },
                            origin: window.location.origin
                        });

                        playersYoutube.push({
                            playerId: $video.attr('id'),
                            player: player
                        });
                    });
                };
            });
        };

        return YoutubeVideos;
    })();

    // Modified from https://stackoverflow.com/a/51789311/5629100
    var VimeoVideos = (function () {
        var queueVimeo = [],		// queue of elements which will become players.
            playersVimeo = [];		// players array (to control players individually)

        // Constructor
        function VimeoVideos() { }

        // Add elements to queue
        VimeoVideos.prototype.add = function ($video) {
            queueVimeo.push($video);
        };

        // Converts queue to players.
        VimeoVideos.prototype.loadPlayers = function () {
            queueVimeo.forEach(function (item, index) {
                var vimeoOptions = {
                    id: $(item).data('video-id'),
                    autoplay: false,
                    muted: ($(item).data('autoplay') == true) ? true : false
                };

                playersVimeo[index] = {
                    playerId: $(item).attr('id'),
                    player: new Vimeo.Player(item, vimeoOptions)
                };
            });
        };

        // Finds player based on the id of the iframe element
        VimeoVideos.prototype.findPlayer = function (id) {
            var playerObj = null;

            playersVimeo.forEach(function (item) {
                if (item.playerId == id) {
                    playerObj = item.player;
                }
            });

            return playerObj;
        };

        // Adds listeners to the players that have been created now so they interact
        // correctly with the header galleries.
        VimeoVideos.prototype.bindListeners = function () {
            playersVimeo.forEach(function (item) {
                item.player.on('play', function () {
                    var $wrapSlideshow = $(this.element.parentNode).parents('.wrap-slideshow');
                    $wrapSlideshow.find('.cycle-pager').fadeOut();
                    $wrapSlideshow.find('.playPause').fadeOut();
                    $wrapSlideshow.find('.headerGalleryV2').cycle('pause');
                });
            });

            playersVimeo.forEach(function (item) {
                item.player.on('pause', function () {
                    var $wrapSlideshow = $(this.element.parentNode).parents('.wrap-slideshow');

                    $(this.element.parentNode).data('paused', true);
                    $wrapSlideshow.find('.cycle-pager').fadeIn();
                    $wrapSlideshow.find('.playPause').fadeIn();
                    $wrapSlideshow.find('.headerGalleryV2').cycle('resume');
                });
            });

            playersVimeo.forEach(function (item) {
                item.player.on('ended', function () {
                    if ($(this.element.parentNode).data('loop-video') == true) {
                        this.play();
                    } else {
                        var $wrapSlideshow = $(this.element.parentNode).parents('.wrap-slideshow');

                        $(this.element.parentNode).data('paused', true);
                        $wrapSlideshow.find('.cycle-pager').fadeIn();
                        $wrapSlideshow.find('.playPause').fadeIn();
                        $wrapSlideshow.find('.headerGalleryV2').cycle('resume');
                    }
                });
            });

            playersVimeo.forEach(function (item) {
                item.player.ready().then(function () {
                    if ($(this).parents('.cycle-slide-active').length > 0 && $(this).data('autoplay') == true) {
                        this.play();
                    }
                });
            });
        };

        return VimeoVideos;
    })();

    // When the Youtube video is paused, ends, or begins, we need to either
    // show or hide the slideshow controls and either pause or resume the
    // slideshow. We also handle looping the Youtube videos here because the
    // Youtube API is not 100% reliable for that function.
    function onPlayerStateChange(e) {
        var $videoElem = null;

        $.each(e.target, function (index, value) {
            if (typeof value === 'object' && $(value).is('iframe')) {
                $videoElem = $(value);
            }
        });

        var $wrapSlideshow = $videoElem.parents('.wrap-slideshow').first();
        var videoState = e.data;

        if (videoState == YT.PlayerState.PAUSED) {
            $videoElem.data('paused', true);
            $wrapSlideshow.find('.cycle-pager').fadeIn();
            $wrapSlideshow.find('.playPause').fadeIn();
            $wrapSlideshow.find('.headerGalleryV2').cycle('resume');
        }

        if (videoState == YT.PlayerState.ENDED) {
            if ($videoElem.data('loop-video') == true) {
                playYoutubePlayerById($videoElem.attr('id'));
            } else {
                $videoElem.siblings('.youtubeVideoThumbnail').show();
                $videoElem.siblings('.youtubeVideoThumbnailPlay').show();
                $wrapSlideshow.find('.cycle-pager').fadeIn();
                $wrapSlideshow.find('.playPause').fadeIn();
                $wrapSlideshow.find('.headerGalleryV2').cycle('resume');
            }
        }

        if (videoState == YT.PlayerState.PLAYING) {
            $videoElem.data('paused', false);
            $videoElem.siblings('.youtubeVideoThumbnail').fadeOut();
            $videoElem.siblings('.youtubeVideoThumbnailPlay').fadeOut();
            $wrapSlideshow.find('.headerGalleryV2').cycle('pause');
            $wrapSlideshow.find('.cycle-pager').fadeOut();
            $wrapSlideshow.find('.playPause').fadeOut();
        }
    }

    function onPlayerReady(e) {
        var $videoElem = null;

        $.each(e.target, function (index, value) {
            if (typeof value === 'object' && $(value).is('iframe')) {
                $videoElem = $(value);
            }
        });

        if ($videoElem.parent('.cycle-slide-active').length > 0 && $videoElem.data('autoplay') == true) {
            e.target.mute();
            forcePlayYoutubePlayer(e.target);
        }
    }

    function pauseYoutubePlayerById(id) {
        var player = youtubes.findPlayer(id);
        player.pauseVideo();

        if (player.getPlayerState() == 1) {
            forcePauseYoutubePlayer(player);
        }
    }

    function forcePauseYoutubePlayer(player) {
        player.pauseVideo();

        if (player.getPlayerState() == 1) {
            setTimeout(function () {
                forcePauseYoutubePlayer(player);
            }, 100);
        }
    }

    function playYoutubePlayerById(id) {
        var player = youtubes.findPlayer(id);
        forcePlayYoutubePlayer(player);
    }

    function muteYoutubePlayerById(id) {
        var player = youtubes.findPlayer(id);
        player.mute();
    }

    function forcePlayYoutubePlayer(player) {
        player.playVideo();

        if (player.getPlayerState() == 1) {
            setTimeout(function () {
                forcePlayYoutubePlayer(player);
            }, 100);
        }
    }

    function pauseVimeoPlayerById(id) {
        var player = vimeos.findPlayer(id);

        if (player != null) {
            player.pause();
        }
    }

    function playVimeoPlayerById(id) {
        var player = vimeos.findPlayer(id);

        if (player != null) {
            player.play();
        }
    }

    function addYouTubeStyles(videoId, containerWidth, containerHeight, videoWidth, videoHeight, videoZoom) {
        var containerRatio = containerWidth / containerHeight;
        var videoRatio = videoWidth / videoHeight;
        var trueSize = $('#' + videoId).parents('.headerGalleryV2').innerWidth() / containerWidth;
        var styles = '#' + videoId + ' {';

        if (containerRatio === videoRatio) { // Video already fits the size of the container.
            styles += 'top: 0;';
            styles += 'left: 0;';
            styles += 'width: 100%;';
            styles += 'height: 100%;';
        } else if (containerRatio > videoRatio) { // Container wider than video, needs to cut off top and bottom of video to scale.
            var scale = (containerWidth / videoWidth);

            styles += 'position: absolute;';
            styles += 'left: 0;';
            styles += 'width: 100%;';

            if (videoZoom === 'nozoom') {
                styles += 'top: 0;';
                styles += 'height: 100%;';
            } else if (videoZoom === 'zoomtop') {
                styles += 'top: 0;';
                styles += 'height: ' + (Math.floor((videoHeight * scale) * trueSize)) + 'px;';
            } else if (videoZoom === 'zoombottom') {
                styles += 'bottom: 0;';
                styles += 'height: ' + (Math.floor((videoHeight * scale) * trueSize)) + 'px;';
            } else if (videoZoom === 'zoommiddle') {
                styles += 'top: 0;';
                styles += 'height: ' + (Math.floor((videoHeight * scale) * trueSize)) + 'px;';
                styles += 'transform: translateY(-' + (((videoHeight * scale) - containerHeight) / (videoHeight * scale) * 50) + '%);'
            }
        } else { // Container taller than video, needs to cut off left and right of video to scale.
            var scale = (containerHeight / videoHeight);

            styles += 'position: absolute;';
            styles += 'left: 0;';
            styles += 'height: 100%;';

            if (videoZoom === 'nozoom') {
                styles += 'top: 0;';
                styles += 'height: 100%;';
            } else if (videoZoom === 'zoomtop') {
                styles += 'top: 0;';
                styles += 'width: ' + (Math.floor((videoWidth * scale) * trueSize)) + 'px;';
            } else if (videoZoom === 'zoombottom') {
                styles += 'bottom: 0;';
                styles += 'width: ' + (Math.floor((videoWidth * scale) * trueSize)) + 'px;';
            } else if (videoZoom === 'zoommiddle') {
                styles += 'top: 0;';
                styles += 'width: ' + (Math.floor((videoWidth * scale) * trueSize)) + 'px;';
                styles += 'transform: translateX(-' + (((videoWidth * scale) - containerWidth) / (videoWidth * scale) * 50) + '%);'
            }
        }

        styles += '}';

        if ($('#youtubeVideo' + videoId + 'Styles').length > 0) {
            $('#youtubeVideo' + videoId + 'Styles').remove();
        }

        var styleTag = $('')
        $('head').append(styleTag);
        $(styleTag).append(styles);
    }

    // Global Variables
    var youtubes = new YoutubeVideos();
    var vimeos = new VimeoVideos();

    $(document).ready(function () {
        $('.youtubeVideoSlide').each(function () {
            youtubes.add($(this));
        });

        youtubes.loadApi();

        $('.vimeoVideoSlide').each(function () {
            vimeos.add($(this));
        });

        vimeos.loadPlayers();
        vimeos.bindListeners();

        $('.wrap-slideshow').on('click', '.youtubeVideoThumbnailPlay, .youtubeVideoThumbnail', function () {
            playYoutubePlayerById($(this).data('player-id'));
        });
    });

    $(document).on('cycle-initialized', function (event, optionHash, outgoingSlideEl, incomingSlideEl, forwardFlag) {
        var youtubeSlide = $('#slideshow .cycle-slide-active').children('.youtubeVideoSlide');
        var vimeoSlide = $('#slideshow .cycle-slide-active').children('.vimeoVideoSlide');

        if (youtubeSlide.length > 0 && youtubeSlide.data('autoplay') == true) {
            youtubeSlide.parents('.headerGalleryV2').cycle('pause');
        }

        if (vimeoSlide.length > 0 && vimeoSlide.data('autoplay') == true) {
            vimeoSlide.parents('.headerGalleryV2').cycle('pause');
            playVimeoPlayerById(vimeoSlide.attr('id'));
        }

        $('.cycle-pager > span').attr('tabindex', 0);
    });

    $(document).ready(function () {
        // For ADA Compliance
        $('.wrap-slideshow').on('keydown', '.prevNext > i, .cycle-pager > span, .playPause > i', function (e) {
            if (e.keyCode === 32 || e.keyCode === 13) {
                e.preventDefault();
                $(this).click();
            }
        });
    });

    $(document).ready(function () {
        $slideshow = $('#slideshow').cycle({
            'pauseOnHover': '#slideshow',
            'slides': '> .slide',
            'pager': '.slideshow.cycle-pager',
            'pagerTemplate': 'slide number {{slideNum}}',
            'autoHeight': '1900:500',
            'timeout': 3000,
            'fx': 'fade',
            'log': false
        });

        setTimeout(function () {
            $('.wrap-slideshow.slideshow .playPause .fa-play').on('click', function () {
                $slideshow.cycle('resume');
                $('#slideshow').data('paused', false);
            });

            $('.wrap-slideshow.slideshow .playPause .fa-pause').on('click', function () {
                $slideshow.cycle('pause');
                $('#slideshow').data('paused', true);
            });

            $('.wrap-slideshow.slideshow .prevNext .fa-chevron-left').on('click', function () {
                $slideshow.cycle('prev');
            });

            $('.wrap-slideshow.slideshow .prevNext .fa-chevron-right').on('click', function () {
                $slideshow.cycle('next');
            });
        }, 500);

        $slideshow.on('cycle-before', function (event, optionHash, outgoingSlideEl, incomingSlideEl, forwardFlag) {
            var youtubeSlide = $('#slideshow .cycle-slide-active').children('.youtubeVideoSlide');
            var vimeoSlide = $('#slideshow .cycle-slide-active').children('.vimeoVideoSlide');

            if (youtubeSlide.length > 0) {
                pauseYoutubePlayerById(youtubeSlide.attr('id'));
            }

            if (vimeoSlide.length > 0) {
                pauseVimeoPlayerById(vimeoSlide.attr('id'));
            }
        });

        $slideshow.on('cycle-after', function (event, optionHash, outgoingSlideEl, incomingSlideEl, forwardFlag) {
            var youtubeSlide = $('#slideshow .cycle-slide-active').children('.youtubeVideoSlide');
            var vimeoSlide = $('#slideshow .cycle-slide-active').children('.vimeoVideoSlide');

            if (youtubeSlide.length > 0 && youtubeSlide.data('autoplay') == true && youtubeSlide.data('paused') != true) {
                muteYoutubePlayerById(youtubeSlide.attr('id'))
                playYoutubePlayerById(youtubeSlide.attr('id'));
            }

            if (vimeoSlide.length > 0 && vimeoSlide.data('autoplay') == true && vimeoSlide.data('paused') != true) {
                playVimeoPlayerById(vimeoSlide.attr('id'));
            }
        });

	
    });

div.script:
		$(document).ready(function () {
			var $links = $('#HomeCircleNav > ul > li');
			var pages = Math.ceil($links.length / 4) - 1;
			var linksOnPage = 4;
			var currentPage = 0;

			$links.each(function () {
				if ($(this).hasClass('without-image')) {
					var a = $(this).children('a').html('');
				}

				$(this).children('a').append('' + $(this).children('a').attr('title') + '');
			});

			$links.slice(0, linksOnPage).addClass('active');

			var $prevButton = $('#homeCirclePrev');
			var $nextButton = $('#homeCircleNext');

			$prevButton.click(function () {
				$links = $('#HomeCircleNav > ul > li');
				$links.removeClass('active');

				if (currentPage == 0) {
					currentPage = pages;

					$links.slice((linksOnPage * currentPage), $links.length).addClass('active');
				} else {
					var start = (--currentPage * linksOnPage);
					$links.slice(start, (start + linksOnPage)).addClass('active');
				}
			});

			$nextButton.click(function () {
				$links = $('#HomeCircleNav > ul > li');
				$links.removeClass('active');

				if (currentPage == pages) {
					currentPage = 0;

					$links.slice(0, linksOnPage).addClass('active');
				} else {
					var start = (++currentPage * linksOnPage);
					$links.slice(start, (start + linksOnPage)).addClass('active');
				}
			});
		});

