	(function($){
		$(document).ready(function() {
			//trigger for toggle button
			$('#ja-promo-bar-476 .icon-toogle').click(function() {
				if(getCookie('ja_promo_bar_476')=='opened') {
					jaClosePromoBar();
				} else {
					jaOpenPromoBar();
				}
			});
			
			//Display countdown
			
			//push page down
            			if(getCookie('ja_promo_bar_476') == 'opened' || getCookie('ja_promo_bar_476') == ''){
				jaPromoBarBody('opened');
				//fix conflict with t3 menu
				jaPromoBarT3Nav('opened');
			}
            			
            //check previous status bar
            if(getCookie('ja_promo_bar_476') == 'closed'){
                jaClosePromoBar();
            }else{
				jaOpenPromoBar();
            }
            
            //hide bar when scroll window
                        $(window).scroll(function(){
                if(getCookie('ja_promo_bar_476') == 'opened' || getCookie('ja_promo_bar_476') == ''){
                    if($(window).scrollTop() > parseInt($('#ja-promo-bar-476').outerHeight())){
                        $('#ja-promo-bar-476').css({opacity:1});
                    }else{
                        $('#ja-promo-bar-476').css({opacity:1});
                    }
                }
            });
						
            //window resize
            $(window).resize(function() {});
			
		});
		
		function jaClosePromoBar(){
			var h = parseInt($('#ja-promo-bar-476').outerHeight());
			$('#ja-promo-bar-476 .inner').animate({'top':'-'+h+'px'}, '400', 'linear');
			$('#ja-promo-bar-476 .inner').hide('400');
			$('#ja-promo-bar-476 .icon-toogle').removeClass('opened').addClass('closed');
			jaPromoBarT3Nav('closed');
			jaPromoBarBody('closed');
			setCookie('ja_promo_bar_476','closed',1);
		}
		
		function jaOpenPromoBar(){
			$('#ja-promo-bar-476 .inner').css('display', 'block');
			$('#ja-promo-bar-476 .inner').show('400', function() {
				$('#ja-promo-bar-476 .inner').animate({'top':0}, '400', 'linear');
				$('#ja-promo-bar-476 .icon-toogle').removeClass('closed').addClass('opened');
				jaPromoBarT3Nav('opened');
				jaPromoBarBody('opened');
				setCookie('ja_promo_bar_476','opened',1);
			});
		}
		
		function jaPromoBarBody(type){
			var ja_promo_bar_height = $('#ja-promo-bar-476').outerHeight();
			var ja_t3_main_nav = 0;
			if($('.navbar-collapse-fixed-top').length && $(window).width()<641){
				ja_t3_main_nav = $('.navbar-collapse-fixed-top').outerHeight() + 10;
			}
            			 if(type=='opened' || type==''){
				$('body').animate({'padding-top': (ja_promo_bar_height + ja_t3_main_nav) + 'px'}, '400', 'linear');
			}else{
				$('body').animate({'padding-top': ja_t3_main_nav+'px'}, '400', 'linear');
			}
            		}
		
		function jaPromoBarT3Nav(type){
			if(!type){
				type = getCookie('ja_promo_bar_476');
			}
			var ja_promo_bar_height = $('#ja-promo-bar-476').outerHeight();
			if($('.navbar-collapse-fixed-top').length){
				if(type=='opened' || type==''){
					$('#t3_mainnav').animate({'top': ja_promo_bar_height+'px'}, '400', 'linear');
				}else{
					$('#t3_mainnav').animate({'top': 0}, '400', 'linear');
				}
			}
		}
		
		//set user cookie
		function setCookie(cname,cvalue,exdays){
			var d = new Date();
			d.setTime(d.getTime()+(exdays*24*60*60*1000));
			var expires = "expires="+d.toGMTString();
			document.cookie = cname + "=" + cvalue + "; " + expires;
		}

		function getCookie(cname){
			var name = cname + "=";
			var ca = document.cookie.split(';');
			for(var i=0; i<ca.length; i++)
			{
				var c = ca[i].trim();
				if (c.indexOf(name)==0) return c.substring(name.length,c.length);
			}
			return "";
		}
	}(jQuery));

