    function getUrlParameter(name) {
        name = name.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');
        var regex = new RegExp('[\\?&]' + name + '=([^&#]*)');
        var results1 = regex.exec(location.search);
        var returnVal = (results1 === null) ? '' : decodeURIComponent(results[1].replace(/\+/g, ' '));

        if (returnVal === '') {
            var results2 = regex.exec(location.hash);
            returnVal = (results2 === null) ? '' : decodeURIComponent(results2[1].replace(/\+/g, ' '));
        }

        return returnVal;
    }

	function googleTranslateElementInitMobile() {
        new google.translate.TranslateElement({ pageLanguage: 'en',  layout: google.translate.TranslateElement.InlineLayout.Mobile }, 'google_translate_element');

        var lang = getUrlParameter('translatelang');

        if (lang != '') {
            var jObj = $('#googleTranslateSelect');
            var db = jObj.get(0);
            jObj.val(lang);
            fireEvent(db, 'change');
        }
    }

    function fireEvent(element, event) {
        if (document.createEventObject) {
            // dispatch for IE
            var evt = document.createEventObject();
            return document.getElementById("googleTranslateSelect").fireEvent('on' + event, evt);
        }
        else {
            // dispatch for firefox + others
            var evt = document.createEvent("HTMLEvents");
            evt.initEvent(event, true, true);
            return !element.dispatchEvent(evt);
        }
    }

