    function updateDD() {
        $("#ddlP").show();
        $("#ddlPSpin").show();
        var SchoolID = $("select.staffDD option:selected").val();
        var HideDepartments = [];
		
        var sdata = {
            'SchoolID': $("select.staffDD option:selected").val()
        };
		
        PageMethods.set_path("/school_staff.aspx");
        PageMethods.LoadDDLList(SchoolID, HideDepartments, function (x) {
            console.log(x);
            $("select.departmentDD").find('option').remove().end();
            $("select.departmentDD").append("All Departments");
            popList(x);
            $("#ddlPSpin").hide(150, function () { $("#ddlP, #helpP").show(); });
            var x = readCookie('staffDD');

            if (x > 0) {
                $('select.departmentDD option').each(function () {
                    if (this.value == x) {
                        $("select.departmentDD").val(x);
                    }
                });
            }

            $('select.departmentDD').val(0);
        });
    }

