form.script:
$sbs_plp_submitEvents_BLOCK_116396 = [];
$(function () {
    //$sbs_plp_searchFilters_BLOCK_116396 = $('#sbs_plp_searchFilters_BLOCK_116396');
    var $sbs_plp_form_BLOCK_116396 = $('#sbs_plp_form_BLOCK_116396');

    $sbs_plp_form_BLOCK_116396.find(':input').not('.sbs_plp_query').bind("keypress", function (event) {
        if (event.keyCode == 13) {
            event.preventDefault();
            return false; // ignore default event
        }
    });
    $sbs_plp_form_BLOCK_116396.find(':input').not(':checkbox').bind('change', function () {
        var doSubmit = true;
        for (var eventId = 0; eventId < $sbs_plp_submitEvents_BLOCK_116396.length; eventId++) {
            var shouldSubmit = $sbs_plp_submitEvents_BLOCK_116396[eventId]();
            if (!shouldSubmit) {
                return false;
            }
        }
        if (doSubmit) {
            $sbs_plp_form_BLOCK_116396.trigger('submit');
        }
    });
    $('#sbs_plp_form_BLOCK_116396 input[type=submit]').bind('click', function () {
        var doSubmit = true;
        for (var eventId = 0; eventId < $sbs_plp_submitEvents_BLOCK_116396.length; eventId++) {
            var shouldSubmit = $sbs_plp_submitEvents_BLOCK_116396[eventId]();
            if (!shouldSubmit) {
                return false;
            }
        }
        if (doSubmit) {
            $sbs_plp_form_BLOCK_116396.trigger('submit');
        }
    });
    $sbs_plp_form_BLOCK_116396.find(':checkbox').bind('click', function () {
        $sbs_plp_form_BLOCK_116396.trigger('submit');
    });
});

