function resizeSlider_ir578() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("0");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("shuffle2-ir578-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("shuffle2-ir578-slider").style.height = Math.max((parseFloat("228")/parseFloat("375") * containerWidth), parseFloat("228")) + "px";
	document.getElementById("shuffle2-ir578-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

br.script:
(function(scripts) {

	for (var i = 0; i < scripts.length; i++) {
		var script = scripts[i];
		if (script.src && script.src.match("wotd2.js")) {
			try {
				var newScript = document.createElement("script");
				var cbName = "cb4452";
				window[cbName] = function(html) {
					newScript.outerHTML = html;
				}
				newScript.src = "/apps/word_of_the_day/hp_word_of_the_day.jsp?cb=" + cbName;
				script.parentNode.replaceChild(newScript, script);
			} catch(e) {
				console.log(e);
			}
			break;
		}
	}

})(document.getElementsByTagName("script"));

body.script:
/*  */

if(window.ActiveXObject) {

	var myLIs = document.getElementById("menu").getElementsByTagName("li");

	for (var i=0; i<myLIs.length; i++) {
		myLIs[i].onmouseover=function() {
			this.className+=" over";
		}

		myLIs[i].onmouseout=function() {
			this.className=this.className.substr(0,this.className.lastIndexOf(" over"));
		}
	}
}

/*  */

html.script:
var curWindowOnLoad = window.onload;
window.onload = function() {
	if(curWindowOnLoad) {
		curWindowOnLoad();
	}
	if(document.getElementsByClassName) {
		var newsImages = document.getElementsByClassName("news-img");
		for(i=0; i<newsImages.length; ++i) {
			newsImages[i].parentNode.style.marginRight = "1px";
		}
		var hpImages = document.getElementsByClassName("hp-img");
		for(i=0; i<hpImages.length; ++i) {
			hpImages[i].parentNode.style.marginRight = "1px";
		}
	}
};
