 function collapseLivesteamVideo()
 {
	  $("#live-stream-section").removeClass("live-stream-section-expand").addClass("live-stream-section");
	  
	 
	   
}

  function expandLivesteamVideo()
 {
	  $("#live-stream-section").removeClass("live-stream-section").addClass("live-stream-section-expand");

}

