$(document).ready(function() {

	$('#user-directory-11627').each(function(){

	var listingTriggers = $(this).find('a.ud-expander-trigger');

		$(listingTriggers).click(function(evt){
			evt.preventDefault();

			var theTrigger = $(this);
			var thisListingExp = $(this).parent().parent().find('.ud-expanded');
			var iconSwaparoo = $(this).find('i.fa');

			$(thisListingExp).slideToggle(function(){

				if($(thisListingExp).is(':visible')){

					$(iconSwaparoo).removeClass('fa-plus').addClass('fa-minus');
					$(theTrigger).css({'background-color':'#ededed'});

				} else {

					$(iconSwaparoo).addClass('fa-plus').removeClass('fa-minus');
					$(theTrigger).css({'background-color':''});
				}

			});



		});

	});

});





























