	function pgScreenSize(){
		var getWidth = $(document).width();
		if (getWidth >= 1024) { // 960+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			modGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			if(!$("#gb-channel-list #gb-channel-icons").length) {
				var gbIcons = $("#gb-icons").clone().html();
				$("#gb-channel-list").append("
" + gbIcons + "");
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView();
			if(!$("#gb-channel-list #gb-channel-icons").length) {
				var gbIcons = $("#gb-icons").clone().html();
				$("#gb-channel-list").append("
" + gbIcons + "");
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			if(!$(".rs-gallery-container").length) { photoGallery(); }
			appView();
			if(!$("#gb-channel-list #gb-channel-icons").length) {
				var gbIcons = $("#gb-icons").clone().html();
				$("#gb-channel-list").append("
" + gbIcons + "");
			}
		}
	}

	function photoGallery(){
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
				// PHOTO GALLERY CONFIGURATION
				$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
					"imageWidth"			: 1500,
					"imageHeight"			: 700,
					"titles"			: "yes",
					"captions"			: "yes",
					"playPause"			: "no",
					"mainControls"			: "yes", /* Changed by Jeremy - Custom Hourly ( Root: Case #00107994 ) */
					"contMoreLink"            	: "no",
					"contMoreLinkText"        	: "READ MORE",
					"descMoreLink"            	: "yes",
					"descMoreLinkText"        	: "READ MORE",
					"bullets"			: "no",
					"bulletControls"			: "no",
					"hoverPause"			: "yes",
					"transitionSpeed"		: 3,
					"transitionDelay"		: 8,
					"onImageLoad"			: function(){ },
					"onTransitionStart"		: function(){ },
					"onTransitionEnd"		: function(){ },
					"watchVidClick"			: function(){ },
					"closeVidClick"			: function(){ },
					"allLoaded"			: function(){ 
						modGallery();
					}
				});
			}
		});
	}

	function modGallery() {
		$(".rs-gallery-desc").addClass("ui-clear");

		if(!$("#sw-content-container10 .rs-gallery-desc-outer").length){
			$("#sw-content-container10 .rs-gallery-desc").wrap("
");

            $('#sw-content-container10 .rs-gallery-desc-outer')
                .before( $('#sw-content-container10 .rs-gallery-control.back') )
                .before( $('#sw-content-container10 .rs-gallery-control.next') );
		}		
	}

	function appView(){
		$("#hp-bottom-region div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		//DOC move mystart buttons

			$("#gb-icons a.last").after($(".sw-mystart-button.signin"));
			$('sign in').appendTo(".sw-mystart-button.signin a");
			$(".sw-mystart-button.signin a span:eq(0)").addClass("icon-font").text("2");
