$(".group752345").colorbox({rel:'group752345', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group752345").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery752345 = $("a[rel=group752345]").colorbox({maxWidth: "80%"});
$("a#openGallery752345").click(function(e){
    e.preventDefault();
    $gallery752345.eq(0).click();
});

