	$(function(){

        $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 3168,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var schools = '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                rsMenu(schoolList);
            }
        });
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); imgResize(); });

		// CHANNEL BAR RESPONSIVE MENU
		$("#gb-channel-list-outer").creativeNavMenu({
			"breakPoint"		: 1023, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
