			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"onLoad"                : function(){ }
		});

		//PAGE LIST MENU
		$("#sp-content").creativePagelistMenu({
			"breakPoint"		: 767, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"folderURL"		: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-pagelist-menu/rs-menu-files/",
			"insertionMethod"	: "prepend", // OPTIONS ARE prepend OR append
			"menuBtnText"		: "Pages",
			"useDefaultCSS"		: "yes"
		});

		// GLOBAL ICONS
		$("#gb-icons").creativeIcons({
			'iconNum'      : '5', // NUMBER OF ICONS
			'iconColor'    : '#861300', // ICON COLOR
			'icon1'        : ['/cms/lib8/MA01907509/Centricity/Template/GlobalAssets/icons/aspen27X27L.jpg', '', 'https://ma-brookline.myfollett.com', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon2'        : ['/cms/lib8/MA01907509/Centricity/template/globalassets/icons/calendar.png', '', '/Page/2', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
