			'icon4'        : ['/cms/lib8/MA01907509/Centricity/template/globalassets/icons/twitter.png', '', 'https://twitter.com/@BrooklinePublic', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon5'        : ['/cms/lib8/MA01907509/Centricity/template/globalassets/icons/facebook.png', '', 'https://www.facebook.com/BrooklinePublicSchools', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'siteID'       : '4',
			'siteAlias'    : 'psb',
