    function rsMenu(schoolList) {
    	var mobileSchools = ''; //added this variable, the for loop below, the 'Our Schools' menu option, and the mobileSchools menu items by Lyndsay - Case 00509551 - 3.22.17

         for(var i in schoolList) { 
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
                    }
         }

    	// MYSTART BAR RESPONSIVE MENU
		$("#sw-mystart-outer").creativeResponsiveMenu({
			"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"folderURL"		: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-menu/rs-menu-files/",
			"useDefaultCSS"		: "yes",
			"slideDirection"		: "top-to-bottom", // OTHER OPTION IS 'left-to-right'
			"menuPosition"		: "relative", // OTHER OPTION IS 'relative'
			"menuButtonParent"	: "#sw-mystart-right",
			"showMenuBtnText"	: "no",
			"menuBtnText"		: "MENU",
			"showMenuInfoText"	: "no",
			"menuInfoText768"	: "",
			"menuInfoText640"	: "",
			"menuInfoText480"	: "",
			"menuInfoText320"	: "",
			"showSearch"		: "no",
			"showMyAccount"		: "yes",
			"showSiteManager"       : "yes",
			"showDistrictHome"	: "no", //Changed from yes by Marie - Case #00105450
			"showSchoolList"	: "no", //Changed from yes by Marie - Case #00105450
			"showChannelList"	: "no",
			"showTranslate"		: "yes",
			"translateId"           : "none",
			"extraAccountItems"	: [], // ARRAY OF BUTTON IDS OR CLASSES
			"extraMenuOptions"	: "Our Schools", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"    : mobileSchools, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"			: "4"
		});
    }
	function tabbedArea() {
		$("#sw-content-container1.ui-hp").addClass("ui-clear").tabBlocks();

		var picOne = '/cms/lib/MA01907509/Centricity/Template/GlobalAssets/images///logos/brookline_notag.png';
		if(!$(".tab-image.one").length) {
			$("#hp-tabs .ui-widget.app.active-app").after("
");	
			$(".tab-image.one").show();
		}

		$("#hp-tabs .tab-button:eq(0)").click(function() {
			$(".tab-image").hide();
			var picOne = '/cms/lib/MA01907509/Centricity/Template/GlobalAssets/images///logos/brookline_notag.png';
			if(!$(".tab-image.one").length) {
				$("#hp-tabs .ui-widget.app.active-app").after("
");	
				$(".tab-image.one").show();
			}
			$(".tab-image.one").show();	
		});

		$("#hp-tabs .tab-button:eq(1)").click(function() {
			$(".tab-image").hide();
			$("#hp-tabs .ui-widget.app.active-app").parent().addClass("ui-clear");
