	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			if(!$("#gb-header #gb-icons").length) {
				$("#gb-search").before($("#gb-icons"));
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			var contHeight = $("#hp-content-middle").height();
			$("#hp-content-middle .hp.column.two").height(contHeight);
			if(!$("#gb-header #gb-icons").length) {
				$("#gb-search").before($("#gb-icons"));
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			$("#hp-content-middle .hp.column.two").height("auto");
			if(!$("#gb-header #gb-icons").length) {
				$("#gb-search").before($("#gb-icons"));
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			$("#hp-content-middle .hp.column.two").height("auto");
			$("#hp-tabs .ui-widget.app .ui-widget-header").each(function() {
				if(!$(this).parent().children(".ui-widget-detail").children(".second-header").length) {
					var secondHeader = $(this).parent().children(".ui-widget-detail");
					$(this).clone().addClass("second-header").prependTo(secondHeader);
				}
			});
			if(!$("#gb-channel-list #gb-icons").length) {
				$("#gb-icons").appendTo("#gb-channel-list");
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
			$("#hp-content-middle .hp.column.two").height("auto");
			$("#hp-tabs .ui-widget.app .ui-widget-header").each(function() {
				if(!$(this).parent().children(".ui-widget-detail").children(".second-header").length) {
					var secondHeader = $(this).parent().children(".ui-widget-detail");
					$(this).clone().addClass("second-header").prependTo(secondHeader);
				}
			});
			if(!$("#gb-channel-list #gb-icons").length) {
				$("#gb-icons").appendTo("#gb-channel-list");
			}
		}
	}

