link.script:
var captionIn = function(){
			$('div.caption').delay(200).fadeIn(600);
		};
var captionOut = function(){
			$('div.caption').fadeOut(300);
		};
$(function(){
	$('.flexslider1').flexslider({
		animation: "slide", 
		controlNav:false,
		initDelay: 4000,
		start:captionIn,
		before:captionOut,
		after:captionIn
	});

}); // end document ready

$(window).load(function(e) {
    setTimeout(function(){ 
        $(".contentbox ul").vgrid({
    		easing: "easeOutQuint",
    		time: 400,
    		delay: 20,
    		wait: 500
    	});
    }, 500);
});

body.script:
( function ( body ) {
	'use strict';
	body.className = body.className.replace( /\btribe-no-js\b/, 'tribe-js' );
} )( document.body );
