function headerRightSwitch(){
    var logo = $('header').find('#logo-area');
    var auxArea = $('header').find('#aux-area');
    var logoText = $('header').find('#logo-text-items');
    var auxMenu = $('header').find('#aux-menu');
    $(logo).insertAfter(auxArea);
    $(logoText).insertAfter(auxMenu);
}

$(document).ready(function() {
	
	var collage = $('#collage');
	var collageLoader = $('#collage-loader');
	var collageClass = $(collage).attr('class');
	var collageImage = $('.collage-image');
	var collageText = $('#collage-text-overlay');
	
	// doc is loaded, fade out the loader...
	$(collageLoader).fadeOut('slow');
	
	// remove minHeight from wrapper
	$(collage).css({
		'minHeight':'0'
	});
	
	// if class string contains 'square'
	if (collageClass.indexOf("square") >= 0) {
		
		// square up
		$(window).on('resize', function () {
				var collageImageWidth = $(collageImage).outerWidth();
				$(collageImage).css({
					'height':Math.round(collageImageWidth)+'px'
				});
		}).resize();
		
	// if class string contains "rect"
	} else if (collageClass.indexOf("rect") >= 0) {
		
		// 16:9
		var collageImageRatio = 0.5625;
		
		$(window).on('resize', function () {
				
				var collageImgNewWidth = $(collageImage).outerWidth();
				var collageImgNewHeight = collageImgNewWidth * collageImageRatio;
				
				$(collageImage).css({
					'height':Math.round(collageImgNewHeight)+'px'
				});
					
		}).resize();
	
	// if class string contains "random"
	} else if (collageClass.indexOf("random") >= 0) {	
	
		var collageRatio = 0.3333333333333333;
		
		$(window).on('resize', function () {
			
			var collageWidth = $(collage).outerWidth();
			var collageNewHeight = collageWidth * collageRatio;
			
			$(collage).css({
				'height':Math.round(collageNewHeight)+'px'
			});
					
		}).resize();
				
	}
	
	// fade them in one at a time for a nice effect
	var collageDelayTally = 0;
	$(collageImage).each(function(i) {
				
		collageDelayTally++;
		$(this).delay(i*250).fadeIn(1500);
	});
	
	// fade in the text, if there is any...
	$(collageText).delay(collageDelayTally*250).fadeIn(1500);
	
			
});

// apply button custom classes...
function customButtonClass1241(container){
	var parent = $(container).parent();
	if ($(parent).hasClass('links-btn-primary')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-primary');
	}
	if ($(parent).hasClass('links-btn-success')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-success');
	}
	if ($(parent).hasClass('links-btn-info')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-info');
	}
	if ($(parent).hasClass('links-btn-warning')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-warning');
	}
	if ($(parent).hasClass('links-btn-danger')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-danger');
	}
	if ($(parent).hasClass('links-btn-lg')){
		$(container).find('.btn').addClass('btn-lg');
	}
	if ($(parent).hasClass('links-btn-sm')){
		$(container).find('.btn').addClass('btn-sm');
	}
	if ($(parent).hasClass('links-btn-xs')){
		$(container).find('.btn').addClass('btn-xs');
	}
}
customButtonClass1241('#links-list-1241');

