				resizeStickyLogo()
			}else{  
				jQuery(".header").sticky({topSpacing:0});
				resizeStickyLogo() 
			}
		}else {
			jQuery(".header").sticky({topSpacing:0});
			resizeStickyLogo()
		}
		jQuery('body.site_boxed .header, body .header_wrap .sticky-wrapper .header').css({"width":jQuery('.header_wrap').width()});
    });
