link.script:
//
var tgs = new Array('div', 'p', 'td', 'pre', 'h*'); // теги, в которых текст будет расти/уменьшаться
var szs = new Array('xx-small', 'x-small', 'small','medium','large','x-large','xx-large'); // размер можно писать в процентах new Array('70%','80%','90%','100%','110%','120%','130%')
var startSz = 3;
function ts(trgt, inc) {
  cEl = null, sz = startSz;
  sz += inc;
  if (sz < 0) sz=0;
  if (sz > 6) sz=6;
  startSz = sz;
  cEl = document.querySelectorAll(trgt);
  for (var c=0; c < cEl.length; c++) {
    cEl[c].style.fontSize = szs[sz];
    for (i=0 ; i < tgs.length; i++) {
      cTags = cEl[c].getElementsByTagName(tgs[i]);
      for (j = 0; j < cTags.length ; j++) cTags[j].style.fontSize=szs[sz];
    }
  }
}
//

var target = document.querySelector('body');
var start = 1;

function incContrast() {
    if (start == 7) return false;

    start += 0.5;
    target.style.filter = 'contrast(' + start + ')';
}

function decContrast() {
    if (start == 1) return false;

    start -= 0.5;
    target.style.filter = 'contrast(' + start + ')';
}

function grayscale() {
    target.style.filter = 'grayscale(1)';
}

function color() {
    target.style.filter = 'grayscale(0)';
}
