$(function() {
  checkCart();
  $('.sqs-add-to-cart-button').on('click', function(){
    setTimeout(function(){
      $('.sqs-modal-lightbox').on('click', 'input.button', function(){
        $('.Cart').removeClass('-empty');
      })
    }, 500);   
  });
});
function checkCart() {
  var cart = $('.Cart'),
      quantity = cart.find('.sqs-cart-quantity').html();
  if (quantity === "0") {
    cart.addClass('-empty');
  } else {    
    cart.removeClass('-empty');
  }
}
