function FB_wait() 
{
    if (typeof jQuery == 'undefined') 
    {  
        window.setTimeout(FB_wait,100);
    }
    else 
    {
        // If jQuery is loaded
        // Make sure body is loaded
        jQuery('document').ready(function () 
            {
                var t=setTimeout("FB_addBudgetBadge()",0);
            });
    }
}//end FB_wait
