		 	function slideit()
		 	{
		 		if (step==1)
		 		{ 			
		 			$("#slide" + step).show("slow");
		 			if (rows>2){
		 				$("#slide" + (rows-1)).hide("slow");
		 				// $("div.social").removeClass("active");
		 			} 
		 		}else{ 					
			 		$("#slide" + step).show("slow");
			 		$("#slide" + (step-1)).hide("slow");
			 		// $("div.social").removeClass("active");	 		
		 		}
		 		if (step < (rows-1)) {
		 			step++;
		 		}else step = 1;

		 		timer=setTimeout("slideit()",8000);
		 	}
			slideit();


