body.script:
// Fixed menu on scroll
$(document).on('scroll', function(){
    if($(document).scrollTop() > 170){
        $('.wrapper').addClass('fixed-nav');
    } else {
        $('.wrapper').removeClass('fixed-nav');
    }
});
