var proc_doe6=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe6(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

