					'	background: -o-linear-gradient(top,  ' + whiteGrad0 + ' 0%,' + whiteGrad100 + ' 100%);' +
					'	background: -ms-linear-gradient(top,  ' + whiteGrad0 + ' 0%,' + whiteGrad100 + ' 100%);' +
					'	background: linear-gradient(to bottom,  ' + whiteGrad0 + ' 0%,' + whiteGrad100 + ' 100%);' +
					'}' +
					'#gb-logo-outer:after {' +
					'	background: url(data:image/svg+xml;base64,);' +
