	function mediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-images").length){
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth" 			: 1500,
		        	"imageHeight" 			: 915,
		        	"appHeader" 			: "no",
		        	"titles" 			: "yes",
		        	"captions" 			: "yes",
		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "no",
		        	"mainControls" 			: "yes",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "Discover More",
		        	"descMoreLink" 			: "yes",
		        	"descMoreLinkText" 		: "Discover More",
		        	"bullets" 			: "no",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "yes", // image dimensions work well at 1500x915
		        	"fullScreenBreakpoints"   	: ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "fade", // fade or slide
		        	"transitionSpeed" 		: 3,
		        	"transitionDelay" 		: 8,
				"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() { },
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() {
		        		$(".cs-img-next").fadeOut("slow");
		        	 },
		        	"onTransitionEnd" 		: function() {
		        		var nextImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.next img").attr("src");
		      			var nextImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.next img").attr("title");

		      			$(".cs-img-next img").attr({
		      				"src"	: nextImgSrc,
		      				"title"	: nextImgTitle,
		      				"alt"	: nextImgTitle
		      			});
		      			$(".cs-img-next").fadeIn();
		        	 },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() {
		        		$("#hp-slideshow .rs-gallery-desc").wrap("
");

		        		if($(".rs-gallery-images-list li").size() > 1) {
			        		var nextImgSrc = $("#hp-slideshow .rs-gallery-images-list .image.next .media-src").text();
			      			var nextImgTitle = $("#hp-slideshow .rs-gallery-images-list .image.next .media-title").text();
			      			$('
Up Next').appendTo("#hp-slideshow .cs-rs-gallery-desc-outer");
			        	 	$(".cs-img-next").fadeIn();

			        	 	$(".cs-img-next").click(function() {
			        	 		$(".rs-gallery-control.next").click();
			        	 	});
			        	}


		        	 }
			});
		}
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-outer div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 span.accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('');
				}
			}
		});

		//DOC accordion header click
		$("#hp-content-outer div.ui-widget-header").off('click').click(function(){
			var clickedApp = $(this).parent();

			if($(clickedApp).hasClass('active')){
				$(clickedApp).removeClass('active');
				$('.accordion-handle', clickedApp).removeClass('point-down').addClass('point-left');
			}else{
				$("#hp-content div.ui-widget.app.active .accordion-handle").removeClass('point-down').addClass('point-left');
				$("#hp-content div.ui-widget.app.active").removeClass("active");

				$('.accordion-handle', clickedApp).removeClass('point-left').addClass('point-down');
				$(this).parent().addClass("active");
			}

			smoothScroll("#" + $(clickedApp).parent().attr("id"), 10);
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//ADD TRANSLATOR
		addFramesetTranslate();

		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC change schoollist selector text
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

		//ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		$(".sw-mystart-dropdown").addClass("transition");

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");

		//DOC if myview button exists, move into myaccount dropdown
		if($(".sw-mystart-button.pw").length) {
			var myviewBtn = $(".sw-mystart-button.pw").html();
			$("
" + myviewBtn + "").prependTo("#sw-myaccount-list");
			$(".sw-mystart-button.pw").hide();
		}

		//DOC move mystart buttons
		$(".sw-mystart-dropdown.schoollist").prependTo("#gb-header-left-top");
		$(".sw-mystart-button.home").prependTo("#gb-header-left-top");
		$("#sw-mystart-search").prependTo(".gb-search-dropdown");

			$(".sw-mystart-button.register").prependTo("#gb-header-right-top");
			$(".sw-mystart-button.signin").prependTo("#gb-header-right-top");


		//SEARCH
		$(".gb-search-btn").click(function() {
			$("#gb-search").toggleClass("open");
			if($("#gb-search").hasClass("open")) {
				$(".gb-search-dropdown").fadeIn();
			} else {
				$(".gb-search-dropdown").fadeOut();
			}
		});

		//POPULAR LINKS
		$(".popular-link-btn").click(function() {
			$("#cs-popular-links").addClass("open");
			if($("#cs-popular-links").hasClass("open")) {
				$(".popular-links-dropdown").show();

				$("#cs-popular-links").hover(function() {

				}, function() {
					$(".popular-links-dropdown").hide();
					$("#cs-popular-links").removeClass("open");
				});
			}
		});

		$(".sw-mystart-dropdown.schoollist").click(function() {
			$(this).addClass("open");
			$(".sw-mystart-dropdown.schoollist").hover(function() {

			}, function() {
				$(this).removeClass("open");
			});
		});

		// ********** SLIDESHOW ********** //
		//SCROLL DOWN BTN
		if($(".hp").length) {
			$("#scroll-down-btn").click(function(){
				$("html, body").animate({
					scrollTop: $("#hp-content-outer").offset().top
				}, 800, function(){
					$("html, body").stop(true, false);
				});
			});
		}

		// ********** HEADER ********** //
		// ADD LOGO
		var logoSrc = jQuery.trim("/cms/lib/MN01909703/Centricity/Template/GlobalAssets/images///logos/NewGoldPaw.png");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
			$("#gb-logo").append('').removeClass("hidden");
		} else {
			$("#gb-logo").append('').removeClass("hidden");
		}

		if('true' == 'true') {
			var typeImg = '/cms/lib/MN01909703/Centricity/Template/GlobalAssets/images///logos/Type-Logo-District.png';
			$("#school-name-cont h1, #school-name-cont h2").hide();
			$("#school-name-cont").append('').removeClass("hidden");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		$(".sw-channel-item").attr("aria-haspopup","true");
		$(".sw-channel-dropdown li").attr("aria-haspopup","false");

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hoverIntent({
			over: function() {
				$(".sw-channel-item ul").stop(true, true);
				var subList = $(this).children('ul');
				if ($.trim(subList.html()) !== "") {
					subList.slideDown(300, "swing");
				}
				$(this).addClass("hover");
			},
			out: function(){
				$(".sw-channel-dropdown", this).slideUp(300, "swing");
				$(this).removeClass("hover");
			},
			timeout: 400
		});

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("VIEW ALL");
		//DOC replace ui-return text for headlines
		$(".headlines.detail .ui-return span").text("« Return to Headlines");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}
		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}
        	//MOD EVENTS
        	$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no",
			dayWeek:"no"
		});
		eventsByDay(".upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
		$(".upcomingevents .joel-month").each(function() {
			var words = $(this).text().split("");
			var text = words.join("");
			$(this).html("" + text + "");
		});
		$(".ui-widget.app.upcomingevents").each(function() {
			var csCalLink = $(this).find(".view-calendar-link");
			var thisHeader = $(this).find(".ui-widget-header h1");
			$(csCalLink).appendTo(thisHeader);
		});
		$("div.ui-widget.app.upcomingevents a.view-calendar-link span").text("View all Events");
        	//APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        	$("div.ui-widget.app .ui-widget-detail img")
        		.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        		.not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        		.each(function() {
        			if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
					$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
				}
		});
		//HP CONTENT TOP HEADLINES
		$("#hp-content-top .ui-widget.app.headlines").each(function() {
			$(".ui-articles li:eq(0)",this).addClass("cs-headline-one");
			$(".ui-articles li:eq(1)",this).addClass("cs-headline-two");
			$(".ui-articles li:eq(2)",this).addClass("cs-headline-three");

			$(".ui-article",this).each(function() {
				var thisArticle = $(this);
				$(this).wrapInner("


");
				if(!$(".ui-article-thumb img",thisArticle).attr("src")) {
					$(".ui-article-thumb",thisArticle).remove();
				} else {
					var articleLI = $(thisArticle).parent();
					$(".ui-article-thumb",thisArticle).prependTo(articleLI);	
				}
			});
		});
		//DOC move view all button for all headlines app into app footer
		$(".ui-widget.app.headlines .ui-articles").each(function() {
			var getMoreLink = $(this).find("a.more-link");
			var getFooter = $(this).parent().parent().parent().parent().find(".ui-widget-footer");
			$(getMoreLink).prependTo(getFooter);
		});
		//CUSTOM SITE SHORTCUTS
	        $("#hp-content-middle #sw-content-container2 .ui-widget.app.siteshortcuts").each(function(){
		        $(".ui-widget-detail .site-shortcuts li", this).each(function(){
			        var imageAlt = $("a img", this).attr("alt");
			        if(imageAlt) {
			        	$("a", this).append('' + imageAlt + '');
			        }
	        	});
	        	$(".ui-widget-header h1",this).html("FIND ITast");
	        });

	        $("#hp-content-bottom-outer div.ui-widget-header h1").wrapInner("");
	        $("#hp-content-top .ui-widget.app.headlines:eq(0)").addClass("first-headlines-app");
	        $("#hp-content-top .ui-widget.app.headlines:eq(1)").addClass("second-headlines-app");

