var jq = jQuery;

jq(document).ready(function(){
    jq(".countdown-active").TimeCircles({
        "animation": "smooth",
        "bg_width": 1,
        "fg_width": 0.05,
        "text_size": .1,
        "count_past_zero": false,
        "circle_bg_color": "#f6bd17",
        "time": {
            "Days": {
                "text": "Days",
                "color": "rgb(255, 255, 255)",
                "show": true
            },
            "Hours": {
                "text": "Hours",
                "color": "rgb(255, 255, 255)",
                "show": true
            },
            "Minutes": {
                "text": "Minutes",
                "color": "rgb(255, 255, 255)",
                "show": true
            },
            "Seconds": {
                "text": "Seconds",
                "color": "rgb(255, 255, 255)",
                "show": true
            }
        }
    });

    //rebuild when window size changes
    jq(window).resize(function() {
        jq(".countdown-active").TimeCircles().rebuild();
    });
});

