$(document).ready(function () {
    try {
        jwplayer("jwPlayer_4eabfbb523ef4a75b43cd7a5388758aa").setup({
            hlshtml: true,
            provider: "video",
            primary: "html5",
            title: "Proposition Greyhounds",
            file: "https://content.schoolinsites.com/api/documents/d6a4fbeb8c824f73884b36ef0193ee30.mp4",
            type: "mp4",
            image: "",
            width: "100%",
            aspectratio: "16:9",
            autostart: false,
            repeat: false,
            controls: true
        }).on('ready', function () {
            jwplayer("jwPlayer_4eabfbb523ef4a75b43cd7a5388758aa").play(false);
        }).on('setupError', function (err) {
            console.log('error setting up video player: ' + err.message);
        });
    }
    catch (err) {
        console.log("video setup error: " + err.message);
    }
});

googletranslate.script:
function googleTranslateElementInit()
{
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        autoDisplay: false,
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');
}

body.script:
    $(document).ready(function () {

    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
