    function partnersSlider() {
    	$(".hp-content.five .ui-widget.app .ui-articles > li.show-partner-false").remove();

    	$(".hp-content.five .cs-app.elementSetRegion").csArticleSlider({
            "viewerNum": [5, 4, 3, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .4 // SECONDS
        });

        $(".hp-content.five #pmi-0 .ui-widget.app.elementSetRegion .cs-article-slider-control").click(function() {
        	$(".hp-content.five #pmi-0 .ui-widget.app.elementSetRegion .cs-article-slider-control").removeClass("clicked");
            if(!$(this).hasClass("clicked")) {
            	$(this).addClass("clicked");
            }
        });

        if($(".hp-content.five .ui-widget.app .ui-articles > li").length > 5) {
          setInterval(function() {
              $(".hp-content.five #pmi-0 .ui-widget.app.elementSetRegion .cs-article-slider-control.next").click();
          }, 4000);
        }
    }

    function homepageHeadlines() {
    	$(".hp .ui-widget.app.headlines .ui-article").each(function() {
            var thisArticle = this;
            if(!$(".cs-article-inner-inner",thisArticle).length) {
                $(thisArticle).wrapInner("
");
                $(".ui-article-title",thisArticle).prependTo(thisArticle);
            }

            if(!$(".cs-article-inner",thisArticle).length) {
                $(thisArticle).wrapInner("
");
                $(".ui-article-thumb",thisArticle).prependTo(thisArticle);
            }

            if(!$(".headline-color-hover",thisArticle).length) {
                $("
").prependTo(thisArticle);
            }

            if($(".cs-article-inner-inner .ui-article-description",thisArticle).text() == "") {
                $(thisArticle).addClass("cs-no-desc");
                $(".custom-teaser-read-more",thisArticle).remove();
            }

            $(".headlines.cs-desktop-headlines li").hover(function() {
                $(".headline-color-hover",this).fadeIn();
            }, function() {
                $(".headline-color-hover",this).fadeOut();
            });
        });

        $(".hp-content.one .ui-widget.app.headlines").each(function() {
        	var thisApp = this;
            if($(".ui-articles li",this).size() > 4) {
            	$('
Load More News').prependTo(".ui-widget-footer",this);
            }
        });

        $(".hp .headlines li .ui-article").not($(".cs-no-desc")).hover(function() {
            $(".cs-article-inner-inner",this).slideDown();
        }, function() {
            $(".cs-article-inner-inner",this).slideUp();
        });

        $(".hp .headlines li .ui-article .ui-article-title a").focus(function() {
            var thisParent = $(this).parent().parent();
            var thisParentParent = $(this).parent().parent().parent();
            $(".cs-article-inner-inner",thisParent).slideDown();
            $(".headline-color-hover",thisParentParent).css({"opacity":".6"});
        });

        $(".hp .headlines li .ui-article .ui-article-title a").focusout(function() {
            var thisParent = $(this).parent().parent();
            var thisParentParent = $(this).parent().parent().parent();
            $(".cs-article-inner-inner",thisParent).slideUp();
            $(".headline-color-hover",thisParentParent).css({"opacity":"0"});
        });

        $(".expand-region-btn").click(function() {
        	if($(this).hasClass("open")) {
            	$(this).attr("aria-label","Load More News Articles").removeClass("open").text("Load More News");
                $(".hp-content.one").removeClass("open");
                //$(".hp-content-container.two").animate({
                $(".hp-content.one .ui-widget.app.headlines .ui-widget-detail").animate({
                	height: $(".hp-content.one .ui-widget.app.headlines .ui-articles > li:first-child").height() + 20
                    //height: "444px"
                }, 500);
            } else {
            	$(this).attr("aria-label","Collapse News Articles").addClass("open").text("Show Less News");
                $(".hp-content.one").addClass("open");
                //$(".hp-content-container.two").animate({height: regionDAppHeight}, 500);
                $(".hp-content.one .ui-widget.app.headlines .ui-widget-detail").animate({
                	height: $(".hp-content.one .ui-widget.app.headlines .ui-articles").height() + 20
                }, 500);
            }
        });

        $(".hp-content.one a").focus(function() {
        	$(".expand-region-btn").attr("aria-label","Collapse News Articles").addClass("open").text("Show Less News");
            $(".hp-content.one").addClass("open");
            $(".hp-content.one .ui-widget.app.headlines .ui-widget-detail").animate({
            	height: $(".hp-content.one .ui-widget.app.headlines .ui-articles").height()
            }, 500);
        })
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				var getChannelHeight = $("#gb-header-outer").height() + 1;
                $("#gb-search").css({
                	'right': '1px',
                    'top': getChannelHeight
                });
                var vidContentHeight = $("#hp-video-content-outer").height() / 2;
                $("#hp-video-content-outer").css({
                	'margin-top': '-' + vidContentHeight
                });
			break;
			case "768":
				var getChannelHeight = $("#gb-header-outer").height() + 1;
                $("#gb-search").css({
                	'right': '1px',
                    'top': getChannelHeight
                });
                var vidContentHeight = $("#hp-video-content-outer").height() / 2;
                $("#hp-video-content-outer").css({
                	'margin-top': '-' + vidContentHeight
                });
			break;
			case "640":
				var getChannelHeight = $("#gb-header-outer").height() + 1;
                $("#gb-search").css({
                	'right': '1px',
                    'top': getChannelHeight
                }).fadeIn();
                var vidContentHeight = $("#hp-video-content-outer").height() / 2;
                $("#hp-video-content-outer").css({
                	'margin-top': '-' + vidContentHeight
                });
                socialMediaAppView();
			break;
			case "480":
				var getChannelHeight = $("#gb-header-outer").height() + 1;
                $("#gb-search").css({
                	'right': '1px',
                    'top': getChannelHeight
                });
                var vidContentHeight = $("#hp-video-content-outer").height() / 2;
                $("#hp-video-content-outer").css({
                	'margin-top': '-' + vidContentHeight
                });
                socialMediaAppView();
			break;
			case "320":
            	var vidContentHeight = $("#hp-video-content-outer").height() / 2;
                $("#hp-video-content-outer").css({
                	'margin-top': '-' + vidContentHeight
                });
                socialMediaAppView();
			break;
		}
	}

    function megaChannelDropdown(){

		//BUILD AN ARRAY OF ELEMENTS
		var channelElements = [
			[//CHANNEL ONE
				["Who We Are",5],
				["Schools/Facilities",5],
				["Programs & Departments",3],
				["Our District","Located just northeast of downtown Kansas City, Liberty Public Schools meets the unique needs of more than 12,800 students. From academics to athletics and gifted to special education, our teachers and administrators collaborate with families to ensure that we provide a future-focused community with a passion for lifelong learning."],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/about lps menu.jpg"]
			],
			[//CHANNEL TWO
				["Enrollment",5],
				["Services",7],
				["Resources",7],
				["For Families","We believe that parents are a child&#39s first and best teacher - and that families play a vital role in their child&#39s academic achievement. Here you will find resources and services to support your experience in Liberty Public Schools."],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/familiesmenu.jpg"]
			],
			[//CHANNEL THREE
				["Resources",7],
				["Resources",2],
				["Academics",4],
				["For Students","As our mission states, we want to inspire and equip all learners to discover their voice and unlimited potential.  It is our top priority to provide safe learning environments for our students, and offer limitless opportunities for success"],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/forstudents.png"]
			],
			[//CHANNEL FOUR
				["Community Relations",3],
				["Getting Involved",5],
				["Resources",8],
				["Staying Connected","We value the importance of connecting with all stakeholders in a variety of methods.  We are committed to providing timely and accurate information to families."],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/Connectedmenu19.jpg"]
			],
			[//CHANNEL FIVE
				["Resources",7],
				["Curriculum",5],
				["Prof. Learning",7],
				["Academic Services","Our goal is to  create opportunities for students and staff to reach their full potential in a Learner-Centered environment."],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/Academicsmenu19.jpg"]
			],
			[//CHANNEL SIX
				["LPS Careers",5],
				["Information",6],
				["LPS Resources",6],
				["Careers","We are dedicated to providing quality, individually-focused support to all employees. We strive to establish a strong sense of commitment to empowering excellence that will be reflected in the growth and success of LPS staff and students. "],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/Careersmenu19.jpg"]
			],
			[//CHANNEL SEVEN
				["Resources",5],
				["Departments",5],
				["Departments",5],
				["For Staff",""],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///megamenu/ForStaffmenu19.jpg"]
			],
            [//CHANNEL EIGHT
				["",],
				["",],
				["",],
				["",""],
                ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"]
			]
		];

		//LOOP THROUGH CHANNELS THAT HAVE DROPDOWNS
		$(".sw-channel-item[aria-haspopup='true']").each(function(){
			var channelNumber = $(this).index();

			//CHECK TO SEE HOW MANY COLUMNS THIS CHANNEL WILL HAVE IN THE DROPDOWN
			//AND HOW MANY SECTIONS WILL BE IN EACH COLUMN
			var numColumns = 0;

			//LOOP THROUGH THE COLUMN ELEMENTS IN THE CHANNEL ARRAY
			for (i = 0; i <= 2; i++) { 
				var columnTitle = channelElements[channelNumber][i][0].trim();
				var numSections = channelElements[channelNumber][i][1];

			    if(columnTitle == ''){
			    	//DO NOTHING

			    } else {
			    	//ADD ONE TO THE numColumns VARIABLE EACH TIME THERE IS TEXT FOUND IN THE COLUMN TITLE
			    	numColumns++;
			    }
			}

			//ONLY PROCEED IF THERE IS MORE THAN 1 COLUMN
			if(numColumns > 1){
				//ADD CLASS TO THE DROPDOWN FOR HOW MANY COLUMNS
				$(".sw-channel-dropdown",this).addClass("column-"+numColumns);

				//LOOP THROUGH EACH COLUMN AND BUILD THE STRUCTURE FOR IT
				var dropDownHTML = '';

                //ADD IMAGE COLUMN
                var dropDownImage = channelElements[channelNumber][4][0];
                dropDownHTML += "
"+
                                    ""+
                                "";

                //ADD CHANNEL CAPTION AND TITLE
                var dropDownCaptionTitle = channelElements[channelNumber][3][0];
                var dropDownCaptionText = channelElements[channelNumber][3][1];
                dropDownHTML += "
"+
                                    "" + dropDownCaptionTitle + ""+
                                    "
" + dropDownCaptionText + "" +
                                "";

				for(i = 0; i < numColumns; i++){
					var numSections = channelElements[channelNumber][i][1];
					var sectionCode = '';


					$(".sw-channel-dropdown li:not(.tagged)", this).each(function(){
						if($(this).index() < numSections){	
							sectionCode += "
"+$(this).html()+"";
							$(this).addClass("tagged");
						}
					})

					//REMOVE SECTIONS THAT WERE ADDED TO NEW STRUCTURE SO WE CAN
					//BEGIN OUR INDEX AT 0 AGAIN THE NEXT TIME THROUGH THE LOOP
					$(".sw-channel-dropdown li.tagged").remove();

					dropDownHTML += "
"+
										""+
										"
"+channelElements[channelNumber][i][0].trim()+""+
											sectionCode+
										""+
									"";
				}

				//ADD DROPDOWN CODE INTO PAGE
				$(".sw-channel-dropdown",this).html(dropDownHTML);
			} else {
				$(this).addClass("normal-dd");
			}
		});

        $(".column-item a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":nth-child(2)")) {
                    	// IS FIRST ITEM IN FIRST COLUMN
                    	if($(this).parent().parent().parent().is(":first-child")){
                        	// FOCUS LAST ITEM IN LAST COLUMN
                        	$("li.sw-channel-item.hover").find(".dropdown-column:nth-last-child(2) .column-item:last-child > a").focus();
                        } else {
                        	// FOCUS LAST ITEM IN PREVIOUS COLUMN
                        	$(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                        }

					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();
					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
                    	//IS LAST ITEM IN LAST COLUMN
                    	if($(this).parent().parent().parent().is(":nth-last-child(2)")){
                        	// FOCUS FIRST ITEM IN FIRST COLUMN
                        	$("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                        } else {
                        	// FOCUS FIRST ITEM IN NEXT COLUMN
                        	$(this).parent().parent().parent().next().find(".column-item:eq(1) > a").focus();
                        }
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
							// FOCUS DROPDOWN BUTTON
							$(".global-icon:last-child").focus();
						} else {
							// FOCUS PREVIOUS ITEM
							$(".sw-channel-item.hover").prev().find("> a").focus();
						}
					} else {
						e.preventDefault();

                        $(".sw-channel-item.hover").next().find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM IN FIRST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM IN LAST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:nth-last-child(2) .column-item:last-child > a").focus();
				break;

				case csKeyCodes.esc:
					$(".sw-channel-item.hover").find("> a").focus();
				break;
			}
		});
	}

    function globalIcons() {
        $("#gb-global-icons-inner").creativeIcons({
                "iconNum"       : "6", // NUMBER OF ICONS
                "icons"         : [
                        ["/cms/lib/MO02207190/Centricity/template/globalassets/icons/liberty-calendar.png", "Calendar", "http://liberty53.tandem.co/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                        ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/icons/liberty-parent.png", "PowerSchool", "https://ps.liberty.k12.mo.us/public/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                        ["/cms/lib/MO02207190/Centricity/Template/GlobalAssets/icons/apply1.png", "Apply to LPS", "https://liberty.tedk12.com/hire/index.aspx", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                        ["/cms/lib/MO02207190/Centricity/template/globalassets/icons/liberty-nutrition.png", "Nutrition Services", "/nutrition", "_blank"],
                        ["/cms/lib/MO02207190/Centricity/template/globalassets/icons/liberty-sprigeo.png", "Sprigeo", "/lpssprigeo", "_blank"],
                        ["/cms/lib/MO02207190/Centricity/template/globalassets/icons/liberty-transportation.png", "Transportation", "/transportation", "_self"]
                ],
                "siteID"        : "4",
                "siteAlias"     : "liberty53",
                "calendarLink"  : "/Page/2",
                "contactEmail"  : "helpdesk@lps53.org",
                "allLoaded"     : function(){
                	$("#gb-global-icons-inner .global-icon").attr("tabindex","-1");
                	$(".gb-fif-btn").click(function() {
                        if($("#gb-global-icons").hasClass("open")) {
                                $(".gb-fif-btn, #find-it-fast-outer").removeClass("open");
                                $("#gb-global-icons").removeClass("open").attr({
                                        "aria-hidden": "true"
                                });
                                $(this).attr({
                                        "aria-expanded": "false"
                                });
                                $("#gb-global-icons-inner .global-icon").attr("tabindex","-1");
                            } else {
                                $(".gb-fif-btn, #find-it-fast-outer").addClass("open");
                                $("#gb-global-icons").addClass("open").attr({
                                        "aria-expanded": "true",
                                        "aria-hidden": "false"
                                });
                                $(this).attr({
                                        "aria-expanded": "true"
                                });
                                $("#gb-global-icons-inner .global-icon").attr("tabindex","0");
                            }
                    });
                    $(".gb-fif-btn").keydown(function(e) {
                        if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                            e.preventDefault();
                            if($("#gb-global-icons").hasClass("open")) {
                                $(".gb-fif-btn, #find-it-fast-outer").removeClass("open");
                                $("#gb-global-icons").removeClass("open").attr({
                                        "aria-hidden": "true"
                                });
                                $(this).attr({
                                        "aria-expanded": "false"
                                });
                                $("#gb-global-icons-inner .global-icon").attr("tabindex","-1");
                            } else {
                                $(".gb-fif-btn, #find-it-fast-outer").addClass("open");
                                $("#gb-global-icons").addClass("open").attr({
                                        "aria-expanded": "true",
                                        "aria-hidden": "false"
                                });
                                $(this).attr({
                                        "aria-expanded": "true"
                                });
                                $("#gb-global-icons-inner .global-icon").attr("tabindex","0");
                                $("#gb-global-icons .global-icon:eq(0)").focus();
                            }
                        }
                    });
                    $("#gb-social-icons .social-icon").focus(function() {
                    	if($("#gb-global-icons").hasClass("open")) {
                            $(".gb-fif-btn, #find-it-fast-outer").removeClass("open");
                            $("#gb-global-icons").removeClass("open").attr({
                                    "aria-expanded": "false",
                                    "aria-hidden": "true"
                            });
                            $("#gb-global-icons-inner .global-icon").attr("tabindex","-1");
                        }
                    });
                }
        });
    }

