    function socialMediaAppView() {
    	if($(".hp").length) {
          $(".hp-content.two #sw-content-container3 > div").each(function() {
              $("
").appendTo(this);
          });

          //DOC accordion header click
          $(".app-accordion-btn").off('click').click(function(){
              var clickedApp = $(this).parent();

              if($(clickedApp).hasClass('active')){
                  $(clickedApp).removeClass('active');
              }else{
                  //$(".hp-content.two #sw-content-container3 > div.active").removeClass("active");
                  $(this).parent().addClass("active");
              }
          });
        }
	}

	function appAccordion() {
    	if($(".hp-content.four .cs-app-header").hasClass("ui-widget-header")) {

        } else {
