input.script:
//
if (typeof(Sys) === 'undefined') throw new Error('ASP.NET Ajax client-side framework failed to load.');
//

div.script:
    $(function () {

        $.ajax({
            type: "POST",
            url: "https://www.nmceaglenation.com//webservices/E74CalendarWebService.asmx/GetCalendarEvents",
	    data: '{OnlyGlobal: \"false\", objSource:\"Google\" , CalendarName:\"ALL_PUBLIC\", StartDate:\"4/26/2020 4:50:09 PM\", EndDate:\"7/26/2020 4:50:09 PM\", EventsShown:\"4\" , DateFormat: \"t\" , DayOfMonthFormat: \"MMM dd\"}',
            contentType: "application/json; charset=utf-8",
            dataType: "json",
            success: OnSuccess,
            failure: function (response) {
                console.log(response.d);
            },

            error: function (response) {
                console.log(response.d);
            }
        });
    });

    function OnSuccess(response) {
        var item = $("#CalendarContainer li").clone(true);

        $("#CalendarContainer li").remove();

        for (var i = 0; i < response.d.length; i++) {
            $(".startdate", item).html(response.d[i]["StartTimeFormatted"]);
            $(".starttime", item).html(response.d[i]["StartTime"]);
            $(".caleventtext", item).html(response.d[i]["Title"] + " - " + response.d[i]["CalendarDisplayName"]);
            $(".title", item).attr("href", "https://www.nmceaglenation.com//EventDetails.aspx?EventSource=Google&CalendarName=" + response.d[i]["CalendarName"] + "&Template=District SecondaryV1&EventID=" + response.d[i]["EventID"]);
            $(".caldate", item).html(response.d[i]["StartTimeDayOfMonth"]);
            $("#CalendarContainer").append(item);
            item = $("#CalendarContainer li:first").clone(true);
        }
    }
