$(document).ready(function() {
	var SciName = "Acer negundo".toLowerCase();
	var ELCODE = "PDACE01090".toUpperCase();
	if ("|gavia immer|gavia pacifica|gavia stellata|gavia adamsii|".indexOf("|" + SciName + "|") > -1) {
		$("#divOtherNHPGuides").show();
		$("#divOtherNHPGuides > div span").off("click").on("click", function() {
			var whichState = $(this).html().toLowerCase();
			var offsiteLink = "";
			switch (whichState) {
				case "idaho": offsiteLink = 'https://idfg.idaho.gov/species/taxa/explore?name="' + SciName + '"'; break;
				case "montana": offsiteLink = "http://fieldguide.mt.gov/search.aspx?q=" + SciName; break;
				case "utah": offsiteLink = "http://fieldguide.wildlife.utah.gov/?Species=" + SciName; break;
				case "wyoming": offsiteLink = "http://fieldguide.wyndd.org/?Species=" + SciName; break;
				case "california": offsiteLink = "https://nrm.dfg.ca.gov/taxaquery/TaxonSearch.aspx?tbxSearchTerm=" + SciName; break;
				case "new york": offsiteLink = "http://www.acris.nynhp.org/search.php?name=" + SciName; break;
				case "south carolina": offsiteLink = "http://dnr.sc.gov/search.html?q=" + SciName; break;
				case "wisconsin": offsiteLink = "https://dnr.wi.gov/topic/EndangeredResources/Animals.asp?mode=detail&SpecCode=" + ELCODE; break;
				case "british columbia": offsiteLink = "http://a100.gov.bc.ca/pub/eswp/reports.do?elcode=" + ELCODE; break;
				case "yukon": offsiteLink = "http://search.gov.yk.ca/search?btnG=Search&site=&client=default_frontend&proxystylesheet=default_frontend&output=xml_no_dtd&lr=lang_en&site=Environment&q=" + SciName; break;
				case "natureserve": offsiteLink = "http://explorer.natureserve.org/servlet/NatureServe?searchName=" + SciName; break;
				case "aaa": offsiteLink = "" + SciName; break;
				case "bbb": offsiteLink = "" + ELCODE; break;
			}
			if (offsiteLink) {
				var testpopup = window.open(offsiteLink);
				if (testpopup == null) { 
					alert("Please be sure to adjust your popup blockers to allow popups from this site."); 
				} else { 
					testpopup.focus(); 
				}
			}
		});
	}
});

