		function showMapper(elcode) {
			var testpopup = window.open('http://mtnhp.org/MapViewer/?t=7&elcode=' + elcode, 'testpopup', 'width=1024, height=768, status=0, toolbar=1, location=1, menubar=1, directories=0, resizable=1, scrollbars=1');
			if (testpopup == null) {
				location.href = 'http://mtnhp.org/MapViewer/?t=7&elcode=' + elcode;
			} else {
				testpopup.focus();
			}
		}

		function showLargeImageUrl(url) {
			var _url = 'http://mtnhp.org/thumbnail/defaultGen.aspx?img=' + url;
			var testpopup = window.open(unescape(url), 'testpopup', 'width=800, height=600, status=0, toolbar=1, location=0, menubar=0, directories=0, resizable=1, scrollbars=1');
			if (testpopup == null) {
				location.href = unescape(_url);
			} else {
				try { testpopup.focus(); } catch(err) {};
			}
		}

