function resizeSlider_ir2821() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("0");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir2821-ir2821-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir2821-ir2821-slider").style.height = Math.max((parseFloat("320")/parseFloat("730") * containerWidth), parseFloat("320")) + "px";
	document.getElementById("ir2821-ir2821-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

img.script:
(function(scripts) {

	for (var i = 0; i < scripts.length; i++) {
		var script = scripts[i];
		if (script.src && script.src.match("wotd2.js")) {
			try {
				var newScript = document.createElement("script");
				var cbName = "cb3115";
				window[cbName] = function(html) {
					newScript.outerHTML = html;
				}
				newScript.src = "/apps/word_of_the_day/hp_word_of_the_day.jsp?cb=" + cbName;
				script.parentNode.replaceChild(newScript, script);
			} catch(e) {
				console.log(e);
			}
			break;
		}
	}

})(document.getElementsByTagName("script"));

