;(function($) {
  // Hide panels that don't have any content in them
  $('#fp-accordion .panel-body').each(function() {
    if ($(this).is(':empty') || $(this).find('> div').hasClass('hide')) $(this).closest('.panel-default').addClass('hidden');
  });

  // If ALL of the panels have been hidden as a result of the code above, hide the entire accordion
  if ($('#fp-accordion .panel-default:visible').length === 0) $('#fp-accordion').addClass('hidden');

  // Open the 1st panel that has content - wait until the DOM is "ready"
  $(function() {
    $('#fp-accordion .panel-default:visible:first .panel-title > a').click();
  });
})(jQuery);

(function($){
  $(function() {

    var divH = 0, maxHeight = 0;

    $('#calendarWeek td div:has(ul)').each(function() {
      var $div = $(this);
      var ulH = $div.find('ul').height();
      divH = $div.height();

      // Get the tallest  in the group
      if (ulH > maxHeight) maxHeight = ulH;

      // If the  is taller than the 
...
      if (ulH > divH) {
        // Which  is this 
 in?
        var tdCount = $div.parent().prevAll().length + 1;

        // Find the same number  in the "more" row
        var $tdMore = $('#calendarWeek tr.more td:nth-child(' + tdCount + ')');

        // Add a "More..." link
        $tdMore.append('More...<\/a>');
      }
    });

    var $uls = $('#calendarWeek td div ul');
    var $links = $('#calendarWeek tr.more td a');
    $links.click(function() {
      var $this = $(this);
      if ($this.text() === 'More...') {
        $uls.each(function() {
          // Animate a "slide down" effect on the parent div's height
          $(this).parent().animate({'height': maxHeight});
        });
        $links.text('...Less');
      } else {
        $uls.each(function() {
          // Animate a "slide up" effect to the parent div's original height
          $(this).parent().animate({'height': divH});
        });
        $links.text('More...');
      }
      $this.blur(); // Lose focus to get rid of the outline
      return false;
    });

  });
})(jQuery);

