	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize();});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		socialIcons();	
		spImage();

	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 320) { // 960+
			photoGallery();
			alertDetect();
			tabbedApps();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1500,
		                "imageHeight"             : 915,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "yes",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
		                "onImageLoad"             : function() {},
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() {
		                				var galleryDescH1 = $("#hp-slideshow .rs-gallery-container .rs-gallery-desc .rs-title.next h1").text();
								var galleryDescP = $("#hp-slideshow .rs-gallery-container .rs-gallery-desc .rs-caption.next p").text();
								$("#new-gallery-desc h1").empty().text(galleryDescH1);
								$("#new-gallery-desc p").empty().text(galleryDescP);
								if($("#new-gallery-desc h1").text() == "" && $("#new-gallery-desc p").text() == ""){
									$("#new-gallery-desc").hide();
								} else {
									$("#new-gallery-desc").show();
								}
		                	},
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { }
		        });
		}
	}	

	function tabbedApps() {	
		//TABBED APPS
        	if(!$(".tabs-container").length){
	        	$("div.hp.column.one div#hp-tabbed-blocks div#sw-content-container3").tabBlocks();
			$(".tab-button:eq(0)").addClass("first");

			var winHeight = $(window).height();
			var headerHeight = $("#gb-header-outer").height();
			var tabHeight = $("#hp-tabbed-blocks .tabs-container").height();
			var footerHeight = $("#gb-footer-outer").height();
			var containerHeight = winHeight - headerHeight - footerHeight;
			var detailHeight = winHeight - headerHeight - footerHeight - 60;
			console.log("The window height is" + winHeight);
			console.log("The header height is" + headerHeight);
			console.log("The footer height is" + footerHeight);
			console.log("The detail height is" + detailHeight);

			$("#hp-content-outer").css("margin-top", headerHeight);
			$("#hp-content .hp.column.one").css("height", containerHeight);

			$(".tab-button").click(function(){
				var widgetHeaderH1 = $("div#hp-tabbed-blocks .tabs-container .tab-button.active span.tab-right").text();
				$("div#hp-tabbed-blocks .ui-widget.app .ui-widget-detail > h1").remove();
				$("div#hp-tabbed-blocks .ui-widget.app .ui-widget-detail").prepend("
" + widgetHeaderH1 + "");

				$("#hp-content .ui-widget.app").css("height", detailHeight - 8);			
				var tabbedBlocks = 0;

				if($("div#hp-tabbed-blocks .tabs-container .tab-button.active").length) {
					$("#hp-slideshow .rs-gallery-container .rs-gallery-overlay").addClass("open");
					$("#hp-content #hp-tabbed-blocks").css("top", "10px");
					//$("#hp-content #hp-tabbed-blocks .tabs-container").css("top", "-6px");
				} else {
					$("#hp-slideshow .rs-gallery-container .rs-gallery-overlay").removeClass("open");
					$("#hp-content #hp-tabbed-blocks").css("top", "auto");
					//$("#hp-content #hp-tabbed-blocks .tabs-container").css("top", "auto");
				}

			});

				$(window).resize(function(){
		                		var winHeight = $(window).height();
					var headerHeight = $("#gb-header-outer").height();
					var tabHeight = $("#hp-tabbed-blocks .tabs-container").height();
					var footerHeight = $("#gb-footer-outer").height();
					var containerHeight = winHeight - headerHeight - footerHeight;
					var detailHeight = winHeight - headerHeight - footerHeight - 60;
					$("#hp-content .hp.column.one").css("height", containerHeight);
					$("#sp-content .sp.column.two").css("height", containerHeight - 30);
					$("#spn-content").css("height", containerHeight - 30);

					$(".tab-button").click(function(){
						$("#hp-content .ui-widget.app").css("height", detailHeight - 8);
					});
					if($("div#hp-tabbed-blocks .tabs-container .tab-button.active").length) {
						$("#hp-content .ui-widget.app").css("height", detailHeight - 8);
					} else {

					}
		                	});

			$(".tab-button.active").removeClass("active");
			$("div#hp-tabbed-blocks .ui-widget.app.active-app").removeClass("active-app");
			$("div#hp-tabbed-blocks").removeClass("hidden");
		}	
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

        function alertDetect() {
        	// ********** ALERT AREA ********** //
		//HIDE ALERT BOX IF HEADER IS EMPTY
		if($("#hp-alert li").size()) {
			$("#hp-alert-outer").removeClass("hidden");
		} else {

		}
        }

	function dynamicContent(){

		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		$("#sw-mystart-search").appendTo("#gb-search-bottom");
		$("#sw-search-button").html('s');
		//$("#sw-mystart-right").clone().appendTo("#gb-header-right #gb-mystart");

